["Hooks"] = {
["Nameplate Removed"] = "function (self, unitId, unitFrame, envTable, modTable)\n    -- Reset the nameplate color to default when removed\n    Plater.RefreshNameplateColor(unitFrame)\nend",
["Nameplate Updated"] = "function (self, unitId, unitFrame, envTable, modTable)\n    -- Reset color if no debuffs are found\n    local foundDebuff = false\n    local highestPriorityColor = nil\n    local highestPriorityIndex = 0\n    \n    -- Check each buff/debuff on the unit\n    for i = 1, #unitFrame.BuffFrame.PlaterBuffList do\n        local auraIconFrame = unitFrame.BuffFrame.PlaterBuffList[i]\n        \n        if auraIconFrame:IsShown() then\n            local currentSpellId = auraIconFrame.SpellId\n            \n            -- Check each configured debuff\n            for index = 1, 9 do\n                local debuffKey = \"debuff\" .. index\n                local colorKey = \"color\" .. index\n                \n                local spellIdToCheck = tonumber(modTable.config[debuffKey])\n                \n                if spellIdToCheck and currentSpellId == spellIdToCheck then\n                    -- If this is a higher priority debuff (higher index), use its color\n                    if index > highestPriorityIndex then\n                        highestPriorityIndex = index\n                        highestPriorityColor = modTable.config[colorKey]\n                        foundDebuff = true\n                    end\n                end\n            end\n        end\n    end\n    \n    -- Update the nameplate color if we found a matching debuff\n    if foundDebuff and highestPriorityColor then\n        modTable.UpdateColor(unitFrame, highestPriorityColor)\n    else\n        -- Reset to default color if no debuffs are found\n        Plater.RefreshNameplateColor(unitFrame)\n    end\nend",
["Initialization"] = "function (modTable)\n    -- Initialize tracking variables\n    modTable.debuffStates = {}\n    \n    -- Function to update the nameplate color based on debuff ID\n    function modTable.UpdateColor(unitFrame, color)\n        if not unitFrame or unitFrame.IsNpcWithoutHealthbar or unitFrame.IsFriendlyPlayerWithoutHealthbar then return end\n        Plater.SetNameplateColor(unitFrame, color)\n    end\n    \n    -- Setup the options panel\n    modTable.options = {\n        -- Debuff-Color Pair 1\n        {\n            type = \"text\",\n            key = \"debuff1\",\n            name = \"Debuff ID #1\",\n            desc = \"Enter the spell ID of the first debuff to track\",\n            value = \"\",\n        },\n        {\n            type = \"color\",\n            key = \"color1\",\n            name = \"Color #1\",\n            desc = \"Choose the color for debuff #1\",\n            value = {1, 0, 0, 1},\n        },\n        -- Debuff-Color Pair 2\n        {\n            type = \"text\",\n            key = \"debuff2\",\n            name = \"Debuff ID #2\",\n            desc = \"Enter the spell ID of the second debuff to track\",\n            value = \"\",\n        },\n        {\n            type = \"color\",\n            key = \"color2\",\n            name = \"Color #2\",\n            desc = \"Choose the color for debuff #2\",\n            value = {0, 1, 0, 1},\n        },\n        -- Debuff-Color Pair 3\n        {\n            type = \"text\",\n            key = \"debuff3\",\n            name = \"Debuff ID #3\",\n            desc = \"Enter the spell ID of the third debuff to track\",\n            value = \"\",\n        },\n        {\n            type = \"color\",\n            key = \"color3\",\n            name = \"Color #3\",\n            desc = \"Choose the color for debuff #3\",\n            value = {0, 0, 1, 1},\n        },\n        -- Debuff-Color Pair 4\n        {\n            type = \"text\",\n            key = \"debuff4\",\n            n