["custom"] = "function(allstates)\n    local currentTime = GetTime()\n    if aura_env.lastTime and currentTime - aura_env.lastTime <= 0.1 then return end\n    aura_env.lastTime = currentTime\n    \n    local Core = aura_env.GetTDACore()\n    if not Core then return end\n    \n    local TriggerAPI = Core.TriggerAPI\n    local DangerLevel = Core.DangerLevel\n    \n    -- Get other trigger states:\n    local S = TriggerAPI.GS(aura_env, {\n            --  Icefury =       {3, \"Icefury\"},     -- \n            Stormkeeper =   {4, \"Storm\"},       -- \n            Ascendance =    {5, \"Ascen\"},       -- \n            Skyfury =       {6, \"Skyfury\"},     -- \n            Spec =          {7, \"Spec\"},        -- Spec (Ele)\n            Primordial =    {8, \"Primordial\"}, -- Primordial wave added\n    })\n    \n    -- Aura logic:\n    for unit in pairs(Core.supportedUnits) do\n        if UnitExists(unit) then\n            local Spec = TriggerAPI.GetStateForUnit(S.Spec, unit)\n            if Spec then\n                -- Self auras:\n                --      local Icefury = TriggerAPI.GetStateForUnit(S.Icefury, unit)\n                local Stormkeeper = TriggerAPI.GetStateForUnit(S.Stormkeeper, unit)\n                local Ascendance = TriggerAPI.GetStateForUnit(S.Ascendance, unit)\n                local Skyfury = TriggerAPI.GetStateForUnit(S.Skyfury, unit)\n                local Primordial = Core.TriggerAPI.GetMatchedAura(S.Primordial, unit, true)\n                \n                \n                \n                -- target Auras:\n                \n                -- Aura display priority:\n                local state = Stormkeeper or Ascendance or Skyfury or Primordial\n                \n                -- Set Icefury or Stormkeeper stacks to state:\n                -- if state then\n                --    state.stacks = Icefury and Icefury.stacks or Stormkeeper and Stormkeeper.stacks or 0\n                --   state.showStacks = true\n                --  end\n                \n                -- Combine aura texts:\n                if state then\n                    if  (Stormkeeper or Ascendance) and Skyfury then\n                        state.combineTexts = { (Stormkeeper or Ascendance).triggernum, Skyfury.triggernum }\n                    else\n                        state.combineTexts = nil\n                    end\n                end\n                \n                -- Calculate danger level:\n                if state then\n                    state.dangerLevel = DangerLevel.Low\n                    \n                    if (Stormkeeper or Ascendance) and Skyfury then\n                        state.dangerLevel = DangerLevel.High\n                    elseif Icefury or Stormkeeper or Skyfury or Primordial then\n                        state.dangerLevel = DangerLevel.Medium\n                    end\n                end\n                \n                -- Get target text:\n                if state then\n                    state.targetText = Core.GetTargetText(aura_env, unit)\n                end\n                \n                -- Add aura:\n                allstates[unit] = state\n            end\n        end\n    end\n    \n    return true\nend",
