["custom"] = "function(allstates)\n    local currentTime = GetTime()\n    if aura_env.lastTime and currentTime - aura_env.lastTime <= 0.1 then return end\n    aura_env.lastTime = currentTime\n    \n    local Core = aura_env.GetTDACore()\n    if not Core then return end\n    \n    -- Get other trigger states:\n    local GS = WeakAuras.GetTriggerStateForTrigger\n    local S = {\n        Magi = GS(aura_env.id, 3),\n        -- ArcanePower = GS(aura_env.id, 4),\n        --RuneOfPower = GS(aura_env.id, 5),\n        --Clearcasting = GS(aura_env.id, 6),\n        ArcaneSurge = GS(aura_env.id,7),\n    }\n    \n    -- Aura logic:\n    for unit in pairs(Core.supportedUnits) do\n        if UnitExists(unit) then\n            -- Self auras:\n            -- local ArcanePower = Core.GetStateForUnit(S.ArcanePower, unit)\n            --local RuneOfPower = Core.GetStateForUnit(S.RuneOfPower, unit)\n            --local Clearcasting = Core.GetStateForUnit(S.Clearcasting, unit)\n            \n            \n            -- Target auras:\n            local Magi = Core.GetMatchedAura(unit, S.Magi, true, false)\n             local ArcaneSurge = Core.GetMatchedAura(unit, S.ArcaneSurge, true, false)\n            \n            -- Aura display priority:\n            local state = Magi or ArcaneSurge\n            \n            -- Calculate danger level:\n            if state then\n                state.dangerLevel = 1\n                \n                if  Magi then\n                    state.dangerLevel = 3\n                elseif ArcaneSurge then\n                    state.dangerLevel = 3\n                end\n            end\n            \n            -- Get target text:\n            if state then\n                state.targetText = Core.GetTargetText(aura_env, unit, Magi and Magi.unit)\n            end\n            \n            -- Add extra text:\n            if state and Magi then\n                state.extraText = Magi and Magi.tooltip1 and AbbreviateNumbers(tonumber(Magi.tooltip1))\n            end\n            \n            -- Add aura:\n            allstates[unit] = state\n        end\n    end\n    \n    return true\nend",
