["custom"] = "function(allstates)\n    local currentTime = GetTime()\n    if aura_env.lastTime and currentTime - aura_env.lastTime <= 0.1 then return end\n    aura_env.lastTime = currentTime\n    \n    local Core = aura_env.GetTDACore()\n    if not Core then return end\n    \n    -- Get other trigger states:\n    local GS = WeakAuras.GetTriggerStateForTrigger\n    local S = {\n        Dance = GS(aura_env.id, 3),\n        Blades = GS(aura_env.id, 4),\n        Symbols = GS(aura_env.id, 5),\n        Prey = GS(aura_env.id, 6),\n        Flag = GS(aura_env.id, 7),\n    }\n    \n    -- Aura logic:\n    for unit in pairs(Core.supportedUnits) do\n        if UnitExists(unit) then\n            -- Self auras:\n            local Dance = Core.GetStateForUnit(S.Dance, unit)\n            local Blades = Core.GetStateForUnit(S.Blades, unit)\n            local Symbols = Core.GetStateForUnit(S.Symbols, unit)\n            \n            -- Target auras:\n            local Prey = Core.GetMatchedAura(unit, S.Prey, true, true)\n            local Flag = Core.GetMatchedAura(unit, S.Flag, true, true)\n            \n            -- Aura display priority:\n            local state\n            if Flag then\n                state = (Flag and Dance) or Flag\n            else\n                state = Blades or Dance\n            end\n            \n            -- Combine aura texts:\n            if state then\n                state.combineTexts = Dance and Blades and Flag and { Dance.triggernum, Blades.triggernum, Flag.triggernum } or nil\n            end\n            \n            -- Calculate danger level:\n            if state then\n                state.dangerLevel = 1\n                \n                if Dance and Blades and Symbols or Dance and Symbols and Flag or Flag then\n                    state.dangerLevel = 3\n                    \n                elseif Blades or Dance and Prey or Dance and Symbols then\n                    state.dangerLevel = 2\n                end\n                \n            end\n            \n            -- Get target text:\n            if state then\n                state.targetText = Core.GetTargetText(aura_env, unit)\n            end\n            \n            -- Add aura:\n            allstates[unit] = state\n        end\n    end\n    \n    return true\nend\n\n\n",
