["subeventSuffix"] = "_CAST_START",
["custom"] = "function(allstates)\n    local currentTime = GetTime()\n    if aura_env.lastTime and currentTime - aura_env.lastTime <= 0.1 then return end\n    aura_env.lastTime = currentTime\n    \n    local Core = aura_env.GetTDACore()\n    if not Core then return end\n    \n    local TriggerAPI = Core.TriggerAPI\n    local DangerLevel = Core.DangerLevel\n    \n    -- Get other trigger states:\n    local S = TriggerAPI.GS(aura_env, {\n            Dragonrage =    {3, \"Dragon\"},      -- Offensive\n            TipTheScales =  {4, \"Empowered\"},   -- Instant + Empowered\n            Spec =          {5, \"Spec\"},        -- Spec (Devastation)\n    })\n    \n    -- Aura logic:\n    for unit in pairs(Core.supportedUnits) do\n        if UnitExists(unit) then\n            local Spec = TriggerAPI.GetStateForUnit(S.Spec, unit)\n            if Spec then\n                -- Self auras:\n                local Dragonrage = TriggerAPI.GetStateForUnit(S.Dragonrage, unit)\n                local TipTheScales = TriggerAPI.GetStateForUnit(S.TipTheScales, unit)\n                \n                -- Aura display priority:\n                local state = Dragonrage or TipTheScales\n                \n                -- Calculate danger level:\n                if state then\n                    state.dangerLevel = DangerLevel.Low\n                    \n                    if Dragonrage and TipTheScales then\n                        state.dangerLevel = DangerLevel.High\n                    elseif Dragonrage or TipTheScales then\n                        state.dangerLevel = DangerLevel.Medium\n                    end\n                end\n                \n                -- Get target text:\n                if state then\n                    state.targetText = Core.GetTargetText(aura_env, unit)\n                end\n                \n                -- Add aura:\n                allstates[unit] = state\n            end\n        end\n    end\n    \n    return true\nend",
