["Ladybug Date"] = {
["outline"] = "OUTLINE",
["wagoID"] = "GRHrJzX7u",
["parent"] = "(: Ladybug :) - Custom Clock & Date",
["displayText_format_p_time_dynamic_threshold"] = 60,
["customText"] = "function()\n    \n    -- ///DISABLE DATE///\n    ---------------------\n    -- If \"Disable Date\" is checked in \"Custom Options\", then make Date disappear by assigning it a nil value\n    if aura_env.config[\"DisableDate\"] then\n        local DisabledDate = nil\n        return DisabledDate\n    end\n    \n    -- ///SHOW DATE AS NUMBERS///\n    -----------------------------\n    -- If \"Number\" is selected from the \"Month Format\" dropdown in \"Custom Options\", then show entire date as numbers    \n    if aura_env.config[\"MonthFormat\"] == 3 then\n        return date(\"%x\")           \n        -- Otherwise define, then show (in the final \"if\" statement) full date with optional Weekday, then Month, then Date, then Year    \n    else\n        local Weekday, Month, Date, Year\n        \n        -- ///DEFINE DATE///\n        -- Day of the month\n        Date=date(\"%d\")\n        -- ///REMOVE THE 0 IN FRONT OF DATES LESS THAN 10///\n        --  \"string.match(\"x\", y) means compare the string in x with the string in y // \"sub(2)\" means start the string at position number 2, thus omitting position number 1 which is the 0\n        if string.match(\"01/02/03/04/05/06/07/08/09\", Date) then \n            Date = Date:sub(2)\n        end\n        \n        -- ///DEFINE FULL WEEKDAY///\n        -- If \"Full\" is selected from the \"Weekday Format\" dropdown in \"Custom Options\", then Weekday is Full\n        if aura_env.config[\"WeekdayFormat\"] == 1 then\n            Weekday=date(\"%A\")\n        else\n            -- ///DEFINE SHORT WEEKDAY///\n            -- Otherwise it is implied \"Short\" was selected from the \"Weekday Format\" dropdown and Weekday is Short // \"..\" means combine the Short Weekday with with a dot at the end since it is abbreviated <- OCD xD\n            Weekday=date(\"%a\")..\".\"\n        end\n        \n        -- ///DEFINE FULL MONTH///\n        -- If \"Full\" is selected from the \"Month Format\" dropdown in \"Custom Options\", then Month is Full\n        if aura_env.config[\"MonthFormat\"] == 1 then\n            Month=date(\"%B\")\n            --///DEFINE SHORT MONTH///\n            -- Otherwise if \"Short\" is selected from the \"Month Format\" dropdown in \"Custom Options\", then Month is Short and with a dot at the end\n        else if aura_env.config[\"MonthFormat\"] == 2 then\n                Month=date(\"%b\")..\".\"\n            end\n        end\n        \n        -- ///DEFINE FULL YEAR///\n        -- If \"Full\" is selected from the \"Year Format\" dropdown in \"Custom Options\", then Year is Full\n        if aura_env.config[\"YearFormat\"] == 1 then\n            Year=date(\"%Y\")\n            -- ///DEFINE SHORT YEAR///\n            -- Otherwise it is implied \"Short\" was selected from the \"Year Format\" dropdown and Year is Short and with an apostrophe in the front\n        else\n            Year=\"'\"..date(\"%y\")\n        end\n        \n        -- ///SHOW WEEKDAY AND FULL DATE///\n        -----------------------------------\n        -- If \"Show Weekday\" is checked in \"Custom Options\", then show Weekday, Month, Date, Year\n        if aura_env.config[\"ShowWeekday\"] then\n            return Weekday..\", \"..Month..\" \"..Date..\", \"..Year\n            \n            -- ///DO NOT SHOW WEEKDAY BUT SHOW DATE///\n            ------------------------------------------\n            -- Otherwise it is implied that \"Show Weekday\" was deselected in \"Custom Options\" and show only Month, Date and Year\n        else\n            return Month..\" \"..Date..\", \"..Year                            \n        end\n    end\nend    \n\n\n\n-- aura_env.config[x] is the direct link to the WeakAuras \"Custom Options\" tab, where \"x\" = \"Option key\" entered in \"Author Mode\" // \"Toggle\" checkboxes have either a \"true\" or \"false\" value and \"Dropdown\" lists have a number attached to the corresponding value. // For example, aura_env.config[\"ShowWeekday\"] = true // not aura_env.config[\"ShowWeekday\"] = false // aura_env.config[\"MonthFormat\"] == 3 means that \"Value 3\" \"Number\" format was selected from the dropdown.\n\n-----------------\n-- FORMAT OPTIONS\n-----------------\n-- Use with date(\"x\") where x (in quotes) is any of the parameters listed below\n-- %c    date and time (e.g., 09/16/98 23:48:10)\n-- %a    abbreviated weekday name (e.g., Wed)\n-- %A    full weekday name (e.g., Wednesday)\n-- %b    abbreviated month name (e.g., Sep)\n-- %B    full month name (e.g., September)\n-- %d    day of the month (16) [01-31]\n-- %w    weekday (3) [0-6 = Sunday-Saturday]\n-- %x    date (e.g., 09/16/98)\n-- %Y    full year (1998)\n-- %y    two-digit year (98) [00-99]\n-- %%    the character `%",
