["subeventPrefix"] = "SPELL",
["subeventSuffix"] = "_CAST_START",
["custom"] = "function(allstates)\n    local currentTime = GetTime()\n    if aura_env.lastTime and currentTime - aura_env.lastTime <= 0.1 then return end\n    aura_env.lastTime = currentTime\n    \n    local Core = aura_env.GetTDACore()\n    if not Core then return end\n    \n    -- Get other trigger states:\n    local GS = WeakAuras.GetTriggerStateForTrigger\n    local S = {\n        DoubleTap = GS(aura_env.id, 3),\n        BL = GS(aura_env.id, 4),\n        Beast = GS(aura_env.id, 5),\n        Aspect = GS(aura_env.id, 6),\n        Assault = GS(aura_env.id, 7),\n        FlayedShot = GS(aura_env.id, 8),\n        TrueShot = GS(aura_env.id, 9), -- Added trueshot\n        Explosive = GS(aura_env.id, 10),  -- Added explosive shot\n        \n        \n    }\n    \n    \n    -- Aura logic:\n    for unit in pairs(Core.supportedUnits) do\n        if UnitExists(unit) then\n            -- Self auras:\n            local DoubleTap = Core.GetStateForUnit(S.DoubleTap, unit)\n            local BL = Core.GetStateForUnit(S.BL, unit)\n            local Beast = Core.GetStateForUnit(S.Beast, unit)\n            local Aspect = Core.GetStateForUnit(S.Aspect, unit)\n            local Assault = Core.GetStateForUnit(S.Assault, unit)\n            local TrueShot = Core.GetStateForUnit(S.TrueShot, unit)\n            -- Target auras:\n            local FlayedShot = Core.GetMatchedAura(unit, S.FlayedShot, true, false)\n            local Explosive = Core.GetMatchedAura(unit, S.Explosive, true, true)  -- Added\n            -- Aura display priority:\n            local state = TrueShot or DoubleTap or Beast or Assault or Explosive \n            -- Calculate danger level:\n            if state then\n                if TrueShot then \n                    state.dangerLevel = 3\n                elseif DoubleTap or BL and Beast and FlayedShot or Aspect and Beast and FlayedShot or Assault and FlayedShot then\n                    state.dangerLevel = 3\n                elseif Beast and FlayedShot or Beast and BL or Beast and Aspect or Assault then\n                    state.dangerLevel = 2\n                elseif Explosive then -- I added this as well here.\n                    state.dangerLevel = 1\n                else\n                    state.dangerLevel = 1\n                end\n            end\n            \n            \n            \n            -- Get target text:\n            if state then\n                state.targetText = Core.GetTargetText(aura_env, unit)\n            end\n            \n            -- Add aura:\n            allstates[unit] = state\n        end\n    end\n    \n    return true\nend\n\n\n\n\n",
