["custom"] = "function(allstates)\n    local currentTime = GetTime()\n    if aura_env.lastTime and currentTime - aura_env.lastTime <= 0.1 then return end\n    aura_env.lastTime = currentTime\n    \n    local Core = aura_env.GetTDACore()\n    if not Core then return end\n    \n    -- Get other trigger states:\n    local GS = WeakAuras.GetTriggerStateForTrigger\n    local S = {\n        --  DecimatingBolt = GS(aura_env.id, 3),\n        -- DemonicCore = GS(aura_env.id, 4),\n        -- EmpoweredDemonicCore = GS(aura_env.id, 5),\n        FromTheShadows = GS(aura_env.id, 6),\n        Tyrant = GS(aura_env.id, 7),\n        Felguard = GS(aura_env.id, 8),\n        DemonicStrength = GS(aura_env.id, 9),\n        Portal = GS(aura_env.id, 10),\n        Pitlord = GS(aura_env.id, 11),\n        \n        \n        \n    }\n    \n    -- Aura logic:\n    for unit in pairs(Core.supportedUnits) do\n        if UnitExists(unit) then\n            -- Self auras:\n            -- local DecimatingBolt = Core.GetStateForUnit(S.DecimatingBolt, unit)\n            -- local DemonicCore = Core.GetStateForUnit(S.DemonicCore, unit)\n            -- local EmpoweredDemonicCore = Core.GetStateForUnit(S.EmpoweredDemonicCore, unit)\n            local Tyrant = Core.GetStateForUnit(S.Tyrant, unit)\n            local Felguard = Core.GetMatchedCast(unit, S.Felguard, true)\n            local DemonicStrength = Core.GetMatchedCast(unit, S.DemonicStrength, true)\n            local Portal = Core.GetMatchedCast(unit, S.Portal, true)\n            local Pitlord = Core.GetMatchedCast(unit, S.Pitlord, true)\n            \n            -- Target auras:\n            local FromTheShadows = Core.GetMatchedAura(unit, S.FromTheShadows, true, true)\n            \n            -- Aura display priority:\n            local state = Tyrant or Portal or Pitlord or DemonicStrength or Felguard \n            \n            -- Set Decimating Bolt stacks to state:\n            --  if state then\n            --    state.stacks = DecimatingBolt and DecimatingBolt.stacks or 0\n            --  state.showStacks = true\n            \n            \n            -- Calculate danger level:\n            if state then\n                state.dangerLevel = 1\n                \n                if  Tyrant then\n                    state.dangerLevel = 3\n                elseif Felguard then\n                    state.dangerLevel = 2\n                    --  elseif DemonicCore then\n                    --    state.dangerLevel = 2\n                elseif DemonicStrength then\n                    state.dangerLevel = 2\n                    \n                elseif Portal then\n                    state.dangerLevel = 2\n                    \n                elseif Pitlord then\n                    state.dangerLevel = 3\n                    \n                    \n                end\n            end\n            \n            -- Get target text:\n            if state then\n                state.targetText = Core.GetTargetText(aura_env, unit)\n            end\n            \n            -- Add aura:\n            allstates[unit] = state\n        end\n    end\n    \n    return true\nend\n\n\n",
