["custom"] = "function(event)\n    local is_in_raid = UnitInRaid(\"player\") ~= nil\n    \n    local cframes = aura_env.config.cframes\n    local cframes_len = table.getn(cframes)\n    \n    if cframes_len >= 1 then\n        for i=1,cframes_len do\n            local frame = _G[cframes[i].name]\n            if frame ~= nil then\n                if cframes[i].hide == 1 or (cframes[i].hide == 2 and is_in_raid) then\n                    frame:Hide()\n                end\n            end\n        end\n    end\n    \n    if aura_env.config[\"uierrormsg\"] then\n        UIErrorsFrame:UnregisterEvent(\"UI_ERROR_MESSAGE\")\n    end\n    \n    if aura_env.config[\"runeframe\"] then\n        RuneFrame:Hide()\n        RegisterStateDriver(RuneFrame, \"visibility\", \"hide\")\n    end\n    \n    if aura_env.config[\"stancebar\"] then\n        StanceBarFrame:Hide()\n        RegisterStateDriver(StanceBarFrame, \"visibility\", \"hide\")\n    end\n    \n    if aura_env.config[\"focus\"] then\n        FocusFrame:Hide()\n        RegisterStateDriver(FocusFrame, \"visibility\", \"hide\")\n    end\n    \n    if aura_env.config[\"targetframebuffs\"] then\n        for i=1, 32 do\n            local frame = _G[\"TargetFrameBuff\"..i]\n            if frame ~= nil then\n                frame:Hide()\n            end\n        end\n    end\n    \nend",
