["trigger"] = {
["type"] = "custom",
["custom_type"] = "stateupdate",
["debuffType"] = "HELPFUL",
["event"] = "Health",
["subeventPrefix"] = "SPELL",
["subeventSuffix"] = "_CAST_START",
["custom"] = "function(allstates)\n    local currentTime = GetTime()\n    if aura_env.lastTime and currentTime - aura_env.lastTime <= 0.1 then return end\n    aura_env.lastTime = currentTime\n    \n    local Core = aura_env.GetTDACore()\n    if not Core then return end\n    \n    local TriggerAPI = Core.TriggerAPI\n    local DangerLevel = Core.DangerLevel\n    \n    -- Get other trigger states (excluding Psyfiend):\n    local S = TriggerAPI.GS(aura_env, {\n            DarkAscension = {3, \"Dark\"},\n            Voidform =      {4, \"Void\"},\n            PI =            {5, \"PI\"},\n            Mindgames =     {7, \"MG\"},\n            DiscArchangel = {8, \"Arch\"}\n    })\n    \n    -- Aura logic:\n    for unit in pairs(Core.supportedUnits) do\n        if UnitExists(unit) then\n            -- Self auras:\n            local DarkAscension = TriggerAPI.GetStateForUnit(S.DarkAscension, unit)\n            local Voidform = TriggerAPI.GetStateForUnit(S.Voidform, unit)\n            local PI = TriggerAPI.GetStateForUnit(S.PI, unit)\n            local DiscArchangel = TriggerAPI.GetStateForUnit(S.DiscArchangel, unit)\n            \n            -- Target auras:\n            local Mindgames = TriggerAPI.GetMatchedAura(S.Mindgames, unit, true, false)\n            \n            -- Aura display priority:\n            local state = DarkAscension or Voidform or DiscArchangel\n            \n            -- Combine aura texts:\n            if state then\n                if (DarkAscension or Voidform) and PI then\n                    state.combineTexts = { DarkAscension and DarkAscension.triggernum or Voidform and Voidform.triggernum, PI.triggernum }\n                else\n                    state.combineTexts = nil\n                end\n            end\n            \n            -- Calculate danger level:\n            if state then\n                state.dangerLevel = DangerLevel.Low\n                \n                if (DarkAscension or Voidform) and (PI or Mindgames) then\n                    state.dangerLevel = DangerLevel.High\n                elseif (DarkAscension or Voidform) then\n                    state.dangerLevel = DangerLevel.Medium\n                elseif DiscArchangel then\n                    state.dangerLevel = DangerLevel.Medium\n                end\n            end\n            \n            -- Get target text:\n            if state then\n                state.targetText = Core.GetTargetText(aura_env, unit)\n            end\n            \n            -- Add aura:\n            allstates[unit] = state\n        end\n    end\n    \n    return true\nend\n\n\n\n\n\n\n",
