["custom"] = "function(allstates, event, ...)\n    \n    aura_env.is_updating = false\n    \n    if event == \"TRAIT_CONFIG_UPDATED\" then\n        \n        aura_env.has_creation_core = aura_env.HasCreationCore()\n        \n        -- Reset all Totems\n        aura_env.recent_totems[0] = nil\n        aura_env.recent_totems[1] = nil\n        \n        aura_env.is_updating = true\n        \n    elseif event == \"UNIT_SPELLCAST_SUCCEEDED\" then\n        \n        local unit = select(1, ...)\n        local spell_id = select(3, ...)\n        \n        if unit == \"player\" then\n            \n            -- Check if Totemic Recall was cast\n            if spell_id == 108285 then\n                \n                -- Reset all Totems\n                aura_env.recent_totems[0] = nil\n                aura_env.recent_totems[1] = nil\n                \n                aura_env.is_updating = true\n                \n                -- Check if a valid Totem was cast\n            elseif aura_env.valid_totems[spell_id] == true then\n                \n                -- Check if Healing Stream Totem was cast twice in a row\n                -- and the previous Totem was valid for Totemic Recall\n                if spell_id == 5394\n                and aura_env.recent_totems[0] == 5394\n                and aura_env.recent_totems[1] ~= nil then\n                    \n                    -- Do nothing\n                    return\n                else\n                    local charges_info = C_Spell.GetSpellCharges(spell_id)\n                    \n                    -- Check if the Spell used has no charges\n                    -- and it is already in the Totemic Recall slot\n                    if not charges_info\n                    and aura_env.recent_totems[0] == spell_id then\n                        \n                        -- Do nothing\n                        return\n                    else \n                        -- Shift the previous Totem and add the current Totem\n                        aura_env.recent_totems[1] = aura_env.recent_totems[0]\n                        aura_env.recent_totems[0] = spell_id\n                    end\n                end\n                \n                aura_env.is_updating = true\n                \n                -- Check if Healing Tide Totem was cast\n            elseif spell_id == 108280 then\n                \n                -- Check if Cloudburst Totem is only in the Creation Core slot\n                if aura_env.recent_totems[0] ~= 157153\n                and aura_env.recent_totems[1] == 157153 then\n                    \n                    -- Reset the Totem in the Creation Core slot\n                    aura_env.recent_totems[1] = nil\n                    \n                    aura_env.is_updating = true\n                end\n            end\n        end\n    end\n    \n    if aura_env.is_updating == true then\n        \n        local spell_id = aura_env.recent_totems[aura_env.slot]\n        \n        if spell_id then\n            \n            local spell_info = C_Spell.GetSpellInfo(spell_id)\n            \n            if spell_info then\n                \n                allstates[\"\"] = {\n                    show = true,\n                    changed = true,\n                    progressType = \"static\",\n                    name = spell_info.name,\n                    icon = spell_info.originalIconID,\n                    spell_id = spell_id,\n                }\n                \n                return true\n            end\n        end\n        \n        allstates[\"\"] = {\n            show = false,\n            changed = true,\n        }        \n        \n        return true\n    end\nend",
