["subeventPrefix"] = "SPELL",
["subeventSuffix"] = "_CAST_START",
["custom"] = "function(allstates)\n    local currentTime = GetTime()\n    if aura_env.lastTime and currentTime - aura_env.lastTime <= 0.1 then return end\n    aura_env.lastTime = currentTime\n    \n    local Core = aura_env.GetTDACore()\n    if not Core then return end\n    \n    -- Get other trigger states:\n    local GS = WeakAuras.GetTriggerStateForTrigger\n    local S = {\n        Ascendance = GS(aura_env.id, 3),\n        Bloodlust = GS(aura_env.id, 4),\n        DoomWinds = GS(aura_env.id, 5),\n        --Windfury = GS(aura_env.id, 6),\n        PrimordialW = GS(aura_env.id, 7)\n    }\n    \n    -- Aura logic:\n    for unit in pairs(Core.supportedUnits) do\n        if UnitExists(unit) then\n            -- Self auras:\n            local Ascendance = Core.GetStateForUnit(S.Ascendance, unit)\n            local Bloodlust = Core.GetStateForUnit(S.Bloodlust, unit)\n            local DoomWinds = Core.GetStateForUnit(S.DoomWinds, unit)\n            --local Windfury = Core.GetStateForUnit(S.Windfury, unit)\n            local PrimordialW = Core.TriggerAPI.GetMatchedAura(S.PrimordialW, unit, true)\n            \n            \n            -- Aura display priority:\n            local state = Ascendance or Bloodlust or DoomWinds or PrimordialW\n            \n            -- Calculate danger level:\n            if state then\n                state.dangerLevel = 1\n                \n                if Ascendance or Bloodlust then\n                    state.dangerLevel = 3\n                elseif DoomWinds then\n                    state.dangerLevel = 2\n                elseif PrimordialW\n                then state.dangerLevel = 2\n            end\n        end\n        \n        -- Get target text:\n        if state then\n            state.targetText = Core.GetTargetText(aura_env, unit)\n        end\n        \n        -- Add aura:\n        allstates[unit] = state\n    end\nend\n\nreturn true\nend",
