["custom"] = "function(allstates)\n    local currentTime = GetTime()\n    if aura_env.lastTime and currentTime - aura_env.lastTime <= 0.1 then \n        return \n    end\n    aura_env.lastTime = currentTime\n    \n    local Core = aura_env.GetTDACore()\n    if not Core then \n        return \n    end\n    \n    -- Get other trigger states:\n    local GS = WeakAuras.GetTriggerStateForTrigger\n    local S = {\n        Final = GS(aura_env.id, 3),\n        Wings = GS(aura_env.id, 4),\n    }\n    \n    -- Aura logic:\n    for unit in pairs(Core.supportedUnits) do\n        if UnitExists(unit) then\n            local Wings = Core.GetStateForUnit(S.Wings, unit)\n            \n            -- Target auras:\n            local Final = Core.GetMatchedAura(unit, S.Final, true, true)\n            \n            -- Aura display priority:\n            local state = Wings or Final\n            \n            -- Calculate danger level:\n            if state then\n                state.dangerLevel = 1\n                \n                if Wings and Final then\n                    state.dangerLevel = 3\n                elseif Wings or Final then\n                    state.dangerLevel = 2\n                end\n            end\n            \n            -- Get target text:\n            if state then\n                state.targetText = Core.GetTargetText(aura_env, unit)\n            end\n            \n            -- Add aura:\n            allstates[unit] = state\n        end\n    end\n    \n    return true\nend\n\n\n\n\n\n\n",
