["adjustedMax"] = "",
["customText"] = "function()\n    if not aura_env.state then return end\n    local Core = aura_env.GetTDACore()\n    if not Core then return end\n    \n    local text = Core.FormatText(aura_env, aura_env.state.triggernum, aura_env.state.targetText) or aura_env.state.name\n    return text and text:upper()\nend",
