["subeventSuffix"] = "_CAST_START",
["custom"] = "function(allstates)\n    local currentTime = GetTime()\n    if aura_env.lastTime and currentTime - aura_env.lastTime <= 0.1 then return end\n    aura_env.lastTime = currentTime\n    \n    local Core = aura_env.GetTDACore()\n    if not Core then return end\n    \n    -- Get other trigger states:\n    local GS = WeakAuras.GetTriggerStateForTrigger\n    local S = {\n        Frenzy = GS(aura_env.id, 3),\n        Berserk = GS(aura_env.id, 4),\n        TF = GS(aura_env.id, 5),\n        Swarm = GS(aura_env.id, 6),\n    }\n    \n    -- Aura logic:\n    for unit in pairs(Core.supportedUnits) do\n        if UnitExists(unit) then\n            -- Self auras:\n            local Berserk = Core.GetStateForUnit(S.Berserk, unit)\n            local TF = Core.GetStateForUnit(S.TF, unit)\n            \n            -- Target auras:\n            local Frenzy = Core.GetMatchedAura(unit, S.Frenzy, true, false)\n            local Swarm = Core.GetMatchedAura(unit, S.Swarm, true, false)\n            \n            -- Aura display priority:\n            local state = Frenzy or Berserk\n            \n            local isFrenzyAndSwarm = Frenzy and Swarm and UnitIsUnit(Frenzy.unit, Swarm.unit)\n            \n            -- Combine aura texts:\n            if state then\n                state.combineTexts = isFrenzyAndSwarm and { Frenzy.triggernum, Swarm.triggernum } or nil\n            end\n            \n            -- Calculate danger level:\n            if state then\n                state.dangerLevel = 1\n                \n                if TF and isFrenzyAndSwarm then\n                    state.dangerLevel = 3\n                elseif isFrenzyAndSwarm then\n                    state.dangerLevel = 2\n                end\n            end\n            \n            -- Get target text:\n            if state then\n                state.targetText = Core.GetTargetText(aura_env, unit, Frenzy and Frenzy.unit)\n            end\n            \n            -- Add aura:\n            allstates[unit] = state\n        end\n    end\n    \n    return true\nend",
