["custom"] = "function(allstates)\n    local currentTime = GetTime()\n    if aura_env.lastTime and currentTime - aura_env.lastTime <= 0.1 then return end\n    aura_env.lastTime = currentTime\n    \n    local Core = aura_env.GetTDACore()\n    if not Core then return end\n    \n    -- Get other trigger states:\n    local GS = WeakAuras.GetTriggerStateForTrigger\n    local S = {\n        ChillStreak = GS(aura_env.id, 3),\n        Pillar = GS(aura_env.id, 4),\n        RuneWeapon = GS(aura_env.id, 5),\n        Badge = GS(aura_env.id, 6),\n    }\n    \n    -- Aura logic:\n    for unit in pairs(Core.supportedUnits) do\n        if UnitExists(unit) then\n            -- Self auras:\n            local Pillar = Core.GetStateForUnit(S.Pillar, unit)\n            local RuneWeapon = Core.GetStateForUnit(S.RuneWeapon, unit)\n            local Badge = Core.GetStateForUnit(S.Badge, unit)\n            \n            -- Casts:\n            local ChillStreak = Core.GetMatchedCast(unit, S.ChillStreak, true)\n            \n            -- Aura display priority:\n            local state = ChillStreak or Pillar or RuneWeapon or Badge\n            \n            -- Combine aura texts:\n            if state then\n                state.combineTexts = ChillStreak and (Pillar or RuneWeapon) and { ChillStreak.triggernum, Pillar and Pillar.triggernum or RuneWeapon and RuneWeapon.triggernum } or nil\n            end\n            \n            -- Calculate danger level:\n            if state then\n                state.dangerLevel = 1\n                \n                if Pillar and (RuneWeapon or ChillStreak or Badge) then\n                    state.dangerLevel = 3\n                elseif ChillStreak or Pillar or RuneWeapon and Badge then\n                    state.dangerLevel = 2\n                end\n            end\n            \n            -- Get target text:\n            if state then\n                state.targetText = Core.GetTargetText(aura_env, unit)\n            end\n            \n            -- Add aura:\n            allstates[unit] = state\n        end\n    end\n    \n    return true\nend",
