["subeventSuffix"] = "_CAST_START",
["custom"] = "function(allstates)\n    local currentTime = GetTime()\n    if aura_env.lastTime and currentTime - aura_env.lastTime <= 0.1 then return end\n    aura_env.lastTime = currentTime\n    \n    local Core = aura_env.GetTDACore()\n    if not Core then return end\n    \n    -- Get other trigger states:\n    local GS = WeakAuras.GetTriggerStateForTrigger\n    local S = {\n        DarkSoul = GS(aura_env.id, 3),\n        Rapid = GS(aura_env.id, 4),\n        -- 5 commented out\n        Phantom = GS(aura_env.id, 6),\n        SoulRot = GS(aura_env.id, 7),\n        Darkglare = GS(aura_env.id, 8),\n        Observer = GS(aura_env.id, 9),\n    }\n    \n    -- Aura logic:\n    for unit in pairs(Core.supportedUnits) do\n        if UnitExists(unit) then\n            -- Self auras:\n            local DarkSoul = Core.GetStateForUnit(S.DarkSoul, unit)\n            local Rapid = Core.GetStateForUnit(S.Rapid, unit)\n            \n            -- Target auras:\n            local Phantom = Core.GetMatchedAura(unit, S.Phantom, true, false)\n            local SoulRot = Core.GetMatchedAura(unit, S.SoulRot, true, false)\n            \n            -- cast\n            local Darkglare = Core.GetMatchedCast(unit, S.Darkglare, true)\n            local Observer = Core.GetMatchedCast(unit, S.Observer, true)\n            \n            -- Aura display priority:\n            local state = Darkglare or Observer or SoulRot \n            \n            -- Calculate danger level:\n            if state then\n                state.dangerLevel = 1\n                \n                if SoulRot then\n                    state.dangerLevel = 3\n                elseif Darkglare then\n                    state.dangerLevel = 3\n                elseif Observer then\n                    state.dangerLevel = 3\n                end\n            end\n            \n            -- Get target text:\n            if state then\n                state.targetText = Core.GetTargetText(aura_env, unit)\n            end\n            \n            -- Add aura:\n            allstates[unit] = state\n        end\n    end\n    \n    return true\nend\n\n\n",
