["custom"] = "local nameMap = {\n    [3] = \"DS\",\n    [4] = \"Rapid\",\n    [5] = \"Drain\",\n    [6] = \"Phantom\",\n    [7] = \"Rot\",\n    [8] = \"Rot\",\n}\n\n-- ###### DO NOT CHANGE BELOW ######\naura_env.triggerNameMap = nameMap\nlocal Core\nlocal failedAttempts, isReported = 0, false\naura_env.SetTDACore = function(TDACore)\n    if not TDACore then return end\n    Core = TDACore\nend\naura_env.GetTDACore = function()\n    if Core and Core.isLoaded then return Core end\n    WeakAuras.ScanEvents(\"TEEA_DANGER_ALERTS_REGISTER\")\n    if not Core or not Core.isLoaded then\n        failedAttempts = failedAttempts + 1\n        if failedAttempts > 2 and not WeakAuras.IsOptionsOpen() and not isReported then\n            isReported = true\n            print(\"|cFFFF0000[ERROR]|r Teea Danger Alert: Core aura is missing!\")\n        end\n        Core = nil\n    end\n    return Core\nend\nCore = aura_env.GetTDACore()\nif Core then Core.TeeaGlowFix(aura_env, aura_env.region) end",
