["Ladybug Clock"] = {
["outline"] = "THICKOUTLINE",
["wagoID"] = "GRHrJzX7u",
["parent"] = "(: Ladybug :) - Custom Clock & Date",
["displayText_format_p_time_dynamic_threshold"] = 60,
["customText"] = "function()\n    \n    -------------------\n    -- ///LOCAL TIME///\n    -------------------\n    -- ///DEFINE \"LOCAL TIME\" IN EITHER \"12-HOUR\" OR \"24-HOUR\" FORMAT///\n    -- If \"Show Realm Time\" is unchecked in \"Custom Options\", then assume \"Local Time\" is to be used // Define \"Minutes\" because \"Local Time\" uses a different clock function than \"Realm Time\" // \"Local Time\" uses \"date(x)\" which returns a string (where \"x\" is \"%M\", \"%I\", \"%p\" or \"%H\"), whereas \"Realm Time\" uses \"GetGameTime()\" which returns a numerical value that needs to be converted into a string thereafter  \n    if not aura_env.config[\"ShowRealmTime\"] then\n        local Time\n        local Minutes = date(\"%M\")\n        \n        -- ///\"12-HOUR\"FORMAT///\n        ------------------------\n        -- If \"12-Hour\" format is selected from the \"Hour Format\" dropdown in \"Custom Options\",  then the \"Time\" displayed is defined\n        if aura_env.config[\"HourFormat\"] == 1 then            \n            -- ///REMOVE THE 0 IN FRONT OF HOURS LESS THAN 10///\n            --  \"string.match(\"x\", y) means compare the string in x with the string in y // \"sub(2)\" means start the string at position number 2, thus omitting position number 1 which is the 0\n            local hourSingleDigitI = date(\"%I\")\n            if string.match(\"01/02/03/04/05/06/07/08/09\", hourSingleDigitI) then \n                hourSingleDigitI = hourSingleDigitI:sub(2)\n            end\n            -- \"date(\"%p\")\" means AM/PM\n            Time=hourSingleDigitI..\":\"..Minutes..\" \"..date(\"%p\") \n            \n            -- ///\"24-HOUR\" FORMAT///\n            -------------------------\n            -- Otherwise, \"24-Hour\" format is implied as selected from the \"Hour Format\" dropdown in \"Custom Options\" and the time displayed is redefined\n        else\n            \n            -- ///REMOVE THE 0 IN FRONT OF HOURS LESS THAN 10///\n            --  \"string.match(\"x\", y) means compare the string in x with the string in y // \"sub(2)\" means start the string at position number 2, thus omitting position number 1 which is the 0\n            local hourSingleDigitH = date(\"%H\")\n            if string.match(\"00/01/02/03/04/05/06/07/08/09\", hourSingleDigitH) then \n                hourSingleDigitH = hourSingleDigitH:sub(2)\n            end            \n            Time=hourSingleDigitH..\":\"..Minutes\n        end\n        -- Show the Time based on the results of the conditions above\n        return Time\n        \n        \n        -------------------\n        -- ///REALM TIME///\n        -------------------\n        \n        -- ///\"12-HOUR\" FORMAT///\n        -------------------------\n        -- If otherwise \"Show Realm Time\" is checked and \"12-Hour\" format is selected from the \"Hour Format\" dropdown in \"Custom Options\", then define \"Realm Time\"\n    else if aura_env.config[\"ShowRealmTime\"] and aura_env.config[\"HourFormat\"] == 1 then\n            -- then GetGameTime() is the \"Realm Time\" in 24-Hour format\n            local hours, minutes = GetGameTime()\n            local AmPm\n            \n            -- ///DEFINE SUFFIX: \"AM\" or \"PM\"///\n            -- If the hours are between 12 and 23 in 24-Hour format, the suffix is \"PM\"\n            if hours >= 12 and hours <= 23 then\n                AmPm=\"PM\"\n                -- Otherwise between the hours 0 and 11 in 24-Hour format, the suffix is \"AM\"\n            else if hours >= 1 and hours <= 11 then\n                    AmPm=\"AM\"\n                    -- At midnight, the suffix is \"AM\"\n                else if hours == 0 then\n                        AmPm=\"AM\"\n                    end\n                end  \n            end\n            \n            -- ///DEFINE \"HOURS\"///\n            -- If the hours in \"24-Hour\" format are greater than or equal to 1 but less than 10, then define \"hours\" // \"tostring\" means convert the numerical value of \"hours\" into text\n            if hours >= 1 and hours <= 11 then\n                hours = tostring(hours)\n                \n                -- Otherwise, if the hours in 24-Hour format are greater or equal to 13, then subtract 12 hours to get the \"12-Hour\" format and convert \"hours\" into text\n            else if hours >= 13 and hours <=23 then\n                    hours = hours - 12\n                    hours = tostring(hours)\n                    \n                    -- At midnight the time in \"24-Hour\" format is 0, so convert it to 12\n                else if hours == 0 then\n                        hours = \"12\"\n                    end\n                end\n            end\n            \n            -- ///DEFINE \"MINUTES\"///\n            -- If the minutes are less than 10, add a 0 in front and convert \"minutes\" to text\n            if (minutes < 10) then\n                minutes = \"0\"..tostring(minutes)\n            end\n            \n            -- ///SHOW TIME///\n            return hours..\":\"..minutes..\" \"..AmPm\n            \n            \n            -- ///\"24-HOUR\" FORMAT///\n            -------------------------\n            -- If otherwise \"Show Realm Time\" is checked and \"24-Hour\" format is selected from the \"Hour Format\" dropdown in \"Custom Options\", then define \"Realm Time\"\n        else if aura_env.config[\"ShowRealmTime\"] and aura_env.config[\"HourFormat\"] == 2 then\n                -- then GetGameTime() = Realm Time in 24-Hour format\n                local hours, minutes = GetGameTime()\n                \n                -- ///DEFINE \"MINUTES\"///\n                -- If the minutes are less than 10, add a 0 in front and convert minutes to text\n                if (minutes < 10) then\n                    minutes = \"0\"..tostring(minutes)\n                end\n                \n                -- ///SHOW TIME///\n                return hours..\":\"..minutes\n            end\n        end\n    end\nend\n\n\n\n-- aura_env.config[x] is the direct link to the WeakAuras \"Custom Options\" tab, where \"x\" = \"Option key\" entered in \"Author Mode\" // \"Toggle\" checkboxes have either a \"true\" or \"false\" value and \"Dropdown\" lists have a number attached to the corresponding Value. // For example, aura_env.config[\"ShowRealmTime\"] = true // not aura_env.config[\"ShowRealmTime\"] = false // aura_env.config[\"HourFormat\"] == 1 means that \"Value 2\" \"24-Hour\" format was selected.\n\n-----------------\n-- FORMAT OPTIONS\n-----------------\n-- Use with date(\"x\") where x (in quotes) is any of the parameters listed below\n-- %c    date and time (e.g., 09/16/98 23:48:10)\n-- %H    hour, using a 24-hour clock (23) [00-23]\n-- %I    hour, using a 12-hour clock (11) [01-12]\n-- %M    minute (48) [00-59]\n-- %m    month (09) [01-12]\n-- %p    either \"am\" or \"pm\" (pm)\n-- %S    second (10) [00-61]\n-- %X    time (e.g., 23:48:10)\n-- %T    hh:mm:ss\n-- %%    the character `%",
