["custom"] = "function(allstates)\n    local currentTime = GetTime()\n    if aura_env.lastTime and currentTime - aura_env.lastTime <= 0.1 then return end\n    aura_env.lastTime = currentTime\n    \n    local Core = aura_env.GetTDACore()\n    if not Core then return end\n    \n    local TriggerAPI = Core.TriggerAPI\n    local DangerLevel = Core.DangerLevel\n    \n    -- Get other trigger states:\n    local S = TriggerAPI.GS(aura_env, {\n            Kyrian =        {3, \"Kyrian\"},      -- +25% Crit Damage from Spear\n            Avatar =        {4, \"Avatar\"},      -- +20% Damage\n            Recklessness =  {5, \"Reckless\"},    -- +20% Crit Chance\n            Roar =          {6, \"Roar\"},        -- Thunderous Roar Bleed\n            Spec =          {7, \"Spec\"},        -- Spec (Fury)\n    })\n    \n    -- Aura logic:\n    for unit in pairs(Core.supportedUnits) do\n        if UnitExists(unit) then\n            local Spec = TriggerAPI.GetStateForUnit(S.Spec, unit)\n            if Spec then\n                -- Self auras:\n                local Kyrian = TriggerAPI.GetStateForUnit(S.Kyrian, unit)\n                local Avatar = TriggerAPI.GetStateForUnit(S.Avatar, unit)\n                local Recklessness = TriggerAPI.GetStateForUnit(S.Recklessness, unit)\n                \n                -- Target auras:\n                local Roar = TriggerAPI.GetMatchedAura(S.Roar, unit, true, true)\n                \n                -- Aura display priority:\n                local state = Recklessness or Kyrian or Avatar or Roar\n                \n                -- Calculate danger level:\n                if state then\n                    state.dangerLevel = DangerLevel.Low\n                    \n                    if (Avatar and Recklessness) or (Kyrian and Recklessness) or (Kyrian and Avatar) then\n                        state.dangerLevel = DangerLevel.High\n                    elseif (Kyrian) or (Avatar) or (Recklessness) then\n                        state.dangerLevel = DangerLevel.Medium\n                    end\n                end\n                \n                -- Get target text:\n                if state then\n                    state.targetText = Core.GetTargetText(aura_env, unit)\n                end\n                \n                -- Add aura:\n                allstates[unit] = state\n            end\n        end\n    end\n    \n    return true\nend\n\n\n\n",
