["custom"] = "function(allstates)\n    local currentTime = GetTime()\n    if aura_env.lastTime and currentTime - aura_env.lastTime <= 0.1 then return end\n    aura_env.lastTime = currentTime\n    \n    local Core = aura_env.GetTDACore()\n    if not Core then return end\n    \n    -- Get other trigger states:\n    local GS = WeakAuras.GetTriggerStateForTrigger\n    local S = {\n        IcyVeins = GS(aura_env.id, 3),\n        IceForm = GS(aura_env.id, 4),\n        Ray = GS(aura_env.id, 6)\n    }\n    \n    -- Aura logic:\n    for unit in pairs(Core.supportedUnits) do\n        if UnitExists(unit) then\n            -- Self auras:\n            local IcyVeins = Core.GetStateForUnit(S.IcyVeins, unit)\n            local IceForm = Core.GetStateForUnit(S.IceForm, unit)\n            \n            -- debuffs:\n            local Ray = Core.GetMatchedAura(unit, S.Ray, true, true)\n            \n            -- Aura display priority:\n            if IcyVeins then\n                local state = IcyVeins\n                state.dangerLevel = 3\n                state.targetText = Core.GetTargetText(aura_env, unit)\n                allstates[unit .. \"-IcyVeins\"] = state\n            end\n            \n            if Ray then\n                local state = Ray\n                state.dangerLevel = 3\n                state.targetText = Core.GetTargetText(aura_env, unit)\n                allstates[unit .. \"-Ray\"] = state\n            end\n            \n            if IceForm then\n                local state = IceForm\n                state.dangerLevel = 3\n                state.targetText = Core.GetTargetText(aura_env, unit)\n                allstates[unit .. \"-IceForm\"] = state\n            end\n        end\n    end\n    \n    return true\nend",
