["customText"] = "function() if WeakAuras.IsOptionsOpen() then return \"SPELL\", ICON_LIST[math.random(1,8)]..\"16|t\" end end",
["sparkRotationMode"] = "AUTO",
["triggers"] = {
{
["trigger"] = {
["type"] = "custom",
["custom_hide"] = "timed",
["custom_type"] = "stateupdate",
["customVariables"] = "{\n    expirationTime = true,\n    recastTimer = \"bool\",\n    category = \"number\",\n    spellID = \"number\",\n}",
["duration"] = "7.4",
["event"] = "Combat Log",
["unit"] = "player",
["subeventSuffix"] = "_CAST_START",
["custom"] = "function(allstates, event, ...)\n    if aura_env.loaded then\n        if event == \"FRAME_UPDATE\" and (not aura_env.last or aura_env.last < GetTime() - 0.5) then\n            aura_env.last = GetTime()\n            local hasUpdates = false\n            if not aura_env.config.hideTimers then\n                for guid, state in pairs(allstates) do\n                    if (state.expirationTime < aura_env.last - aura_env.config.duration) then\n                        if aura_env.timer[guid] then\n                            aura_env.timer[guid]:Cancel()\n                            aura_env.timer[guid] = nil\n                        end\n                        state.show = false\n                        state.changed = true\n                        hasUpdates = true\n                    end\n                end\n            end\n            for _, plate in pairs(C_NamePlate.GetNamePlates()) do\n                local unit = plate.namePlateUnitToken\n                if UnitAffectingCombat(unit) then\n                    local guid = UnitGUID(unit)\n                    if guid and not aura_env.scanned[guid] then\n                        local npcID = select(6, strsplit(\"-\", guid))\n                        local timers = aura_env.timers[npcID] or CauseseDB.timers[npcID]\n                        if timers and next(timers) then\n                            aura_env.scanned[guid] = true\n                            for spellID, timerData in pairs(timers) do\n                                local _, category, role, name, initialTimer = unpack(timerData)\n                                local myRole = UnitGroupRolesAssigned(\"player\")\n                                if (role == \"ALL\" or role == myRole) and category and name and initialTimer then\n                                    local mark = GetRaidTargetIndex(unit)\n                                    local spell = C_Spell.GetSpellInfo(spellID)\n                                    hasUpdates = true\n                                    allstates[guid..spellID] = {\n                                        show = true,\n                                        changed = true,\n                                        name = name,\n                                        guid = guid,\n                                        spellID = spellID,\n                                        icon = spell and spell.iconID,\n                                        category = category,\n                                        progressType = \"timed\",\n                                        duration = initialTimer,\n                                        expirationTime = initialTimer + GetTime(),\n                                        recastTimer = false,\n                                        mark = (mark and ICON_LIST[mark]..\"16|t\") or \"\",\n                                        autoHide = aura_env.config.hideTimers,\n                                    }\n                                end\n                            end\n                        end\n                    end\n                end\n            end\n            return hasUpdates\n        elseif event == \"UNIT_SPELLCAST_SUCCEEDED\" then\n            local unit, _, spellID = ...\n            local sourceGUID = UnitGUID(unit)\n            if sourceGUID then\n                local npcID = select(6, strsplit(\"-\", sourceGUID))\n                local timerData = (aura_env.timers[npcID] and aura_env.timers[npcID][spellID]) or (CauseseDB.timers[npcID] and CauseseDB.timers[npcID][spellID])\n                if timerData then\n                    local timerEvent, category, role, name, _, recastTimer, delay = unpack(timerData)\n                    local myRole = UnitGroupRolesAssigned(\"player\")\n                    if (role == \"ALL\" or role == myRole) and timerEvent == event and category and name and recastTimer then\n                        aura_env.timer[sourceGUID] = aura_env.timer[sourceGUID] or {}\n                        if aura_env.timer[sourceGUID][spellID] then\n                            aura_env.timer[sourceGUID][spellID]:Cancel()\n                            aura_env.timer[sourceGUID][spellID] = nil\n                        end\n                        aura_env.timer[sourceGUID][spellID] = C_Timer.NewTimer(delay or 0, function() WeakAuras.ScanEvents(\"CAUSESE_TIMER\", sourceGUID, spellID, category, name, recastTimer, delay) end)\n                    end\n                end\n            end\n        elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n            local timestamp, subEvent, hideCaster, sourceGUID, sourceName, sourceFlags, sourceRaidFlags, destGUID, destName, destFlags, destRaidFlags, spellID = ...\n            if (subEvent == \"SPELL_CAST_START\" or subEvent == \"SPELL_CAST_SUCCESS\") then\n                local npcID = select(6, strsplit(\"-\", sourceGUID))\n                local timerData = (aura_env.timers[npcID] and aura_env.timers[npcID][spellID]) or (CauseseDB.timers[npcID] and CauseseDB.timers[npcID][spellID])\n                if timerData then\n                    local timerEvent, category, role, name, _, recastTimer, delay = unpack(timerData)\n                    local myRole = UnitGroupRolesAssigned(\"player\")\n                    if (role == \"ALL\" or role == myRole) and timerEvent == subEvent and category and name and recastTimer  then\n                        aura_env.timer[sourceGUID] = aura_env.timer[sourceGUID] or {}\n                        if aura_env.timer[sourceGUID][spellID] then\n                            aura_env.timer[sourceGUID][spellID]:Cancel()\n                            aura_env.timer[sourceGUID][spellID] = nil\n                        end\n                        aura_env.timer[sourceGUID][spellID] = C_Timer.NewTimer(delay or 0, function() WeakAuras.ScanEvents(\"CAUSESE_TIMER\", sourceGUID, spellID, category, name, recastTimer, delay) end)\n                    end\n                end\n            elseif subEvent == \"UNIT_DIED\" then\n                local hasUpdates = false\n                for _, state in pairs(allstates) do\n                    if state.spellID and aura_env.timer[destGUID] and aura_env.timer[destGUID][state.spellID] then\n                        aura_env.timer[destGUID][state.spellID]:Cancel()\n                        aura_env.timer[destGUID][state.spellID] = nil\n                    end\n                    if state.guid == destGUID then\n                        hasUpdates = true\n                        state.show = false\n                        state.changed = true    \n                    end\n                end\n                return hasUpdates\n            end\n        elseif event == \"CAUSESE_TIMER\" and ... then\n            local guid, spellID, category, name, recastTimer, delay = ...\n            delay = delay or 0\n            if guid and spellID and recastTimer then\n                local unit = UnitTokenFromGUID(guid)\n                if unit and UnitAffectingCombat(unit) then\n                    --if unit and UnitAffectingCombat(unit) then\n                    local mark = GetRaidTargetIndex(unit)\n                    local spell = C_Spell.GetSpellInfo(spellID)\n                    allstates[guid..spellID] = {\n                        show = true,\n                        changed = true,\n                        name = name,\n                        guid = guid,\n                        spellID = spellID,\n                        icon = spell and spell.iconID,\n                        category = category,\n                        spellID = spellID,\n                        progressType = \"timed\",\n                        duration = recastTimer-delay,\n                        expirationTime = recastTimer-delay + GetTime(),\n                        recastTimer = true,\n                        mark = (mark and ICON_LIST[mark]..\"16|t\") or \"\",\n                        autoHide = aura_env.config.hideTimers,\n                    }\n                    return true\n                end\n            end\n        elseif event == \"CHALLENGE_MODE_START\" or event == \"WA_DELAYED_PLAYER_ENTERING_WORLD\" then\n            aura_env.scanned = {}\n            aura_env.timer = {}\n        elseif event == \"RAID_TARGET_UPDATE\" then\n            local hasUpdates = false\n            for timer, state in pairs(allstates) do\n                local unit = UnitTokenFromGUID(state.guid)\n                if unit then\n                    hasUpdates = true\n                    local mark = GetRaidTargetIndex(unit)\n                    state.mark = (mark and ICON_LIST[mark]..\"16|t\") or \"\"\n                    state.changed = true\n                end\n            end\n            return hasUpdates\n        end\n    end\nend",
