["custom"] = "aura_env.loaded = C_AddOns.IsAddOnLoaded(\"CauseseDB\")\nif aura_env.loaded then\n    --categoryNumber can be used to differentiate between casts in the conditions for coloring etc\n    --role can be (ALL/DAMAGER/HEALER/TANK/MELEE/RANGE) and only shows the bar to that role\n    \n    --[spellID] = {\"name\",category,\"soundFile\",\"role\", \"show target\" (true/false), \"important\" (true/false)},\n    aura_env.spellIDs = {\n        --spellIDs moved to CauseseDB\n        \n        --[257063] = {\"BOLT\",2,\"\",\"ALL\",true,false}, --Example Spell\n        \n        \n        \n    }\n    \n    -- this is only for spells where the mobs cast it for roleplay purposes and they should only show if target is in party\n    aura_env.rpspellIDs = {\n    }\n    \n    --[[\ndefault logic:\n- category 0 is for important interruptible casts\n- category 1 is for interruptible casts\n- category 2 is for ccable and not interruptible casts\n]]\nend",
