["customText"] = "function()\n    --this is for the preview, applied via conditions\n    if WeakAuras.IsOptionsOpen() then \n        local display = aura_env.config.display \n        if display == 1 then\n            return string.format(\"%s%s%s\", \"\", \"BOLT\", \" - \" .. WA_ClassColorName(\"player\")), ICON_LIST[math.random(1,8)]..\"16|t\"\n        elseif display == 2 then\n            return \"Web Bolt\", ICON_LIST[math.random(1,8)]..\"16|t\"\n        elseif display == 3 then\n            return string.format(\"%s%s%s\", \"\", \"Web Bolt\", \" - \" .. WA_ClassColorName(\"player\")), ICON_LIST[math.random(1,8)]..\"16|t\"\n        end\n        \n    elseif aura_env.state then\n        local display = aura_env.config.display \n        if (display == 1 or display == 3) then\n            if aura_env.state.targetName then --targets a player\n                return string.format(\"%s%s\", display == 1 and aura_env.state.spell or aura_env.state.spellName, \" - \"..aura_env.state.targetName)\n            else\n                return string.format(\"%s\", display == 1 and aura_env.state.spell or aura_env.state.spellName)\n            end\n        elseif display == 2 then\n            return string.format(\"%s\", aura_env.state.spellName)\n        end\n    end\nend",
