["events"] = "UNIT_SPELLCAST_START:nameplate, UNIT_SPELLCAST_STOP:nameplate, UNIT_SPELLCAST_CHANNEL_START:nameplate, UNIT_SPELLCAST_CHANNEL_STOP:nameplate, UNIT_TARGET:nameplate, CAUSE_DELAY_CHANNEL, CAUSE_DELAY_CAST, NAME_PLATE_UNIT_ADDED, NAME_PLATE_UNIT_REMOVED",
["event"] = "Cast",
