["castType"] = "cast",
["type"] = "custom",
["custom"] = "function(allstates, event, ...)\n    if aura_env.loaded then\n        if (event == \"CAUSE_DELAY_CAST\" or event == \"CAUSE_DELAY_CHANNEL\" or event == \"UNIT_TARGET\") and aura_env.config.display ~= 2 then\n            local unit, guid, showTarget = ...\n            local spellID = select(6, CauseseAPI:GetCastInfo(unit))\n            if spellID then\n                guid = guid or UnitGUID(unit)\n                local state = allstates[guid]\n                if state then\n                    local mark = GetRaidTargetIndex(unit)\n                    local isPlayer = UnitIsPlayer(unit..\"target\")\n                    local spell = aura_env.spellIDs[spellID] or CauseseDB.trash_cc[spellID]\n                    showTarget = showTarget or select(5, unpack(spell))\n                    state.targetName = showTarget and (isPlayer and WA_ClassColorName(unit..\"target\") or \"PET\")\n                    state.targetIsMe = UnitIsUnit(unit..\"target\", \"player\")\n                    state.targetRole = UnitGroupRolesAssigned(unit..\"target\")\n                    state.changed = true\n                    return true\n                end\n            end\n            \n        elseif event == \"UNIT_SPELLCAST_START\"\n        or (event == \"UNIT_SPELLCAST_CHANNEL_START\" and aura_env.config.channel)\n        or event == \"NAME_PLATE_UNIT_ADDED\" then\n            local unit = ...\n            local name, duration, expirationTime, icon, notInterruptible, spellID, castType = CauseseAPI:GetCastInfo(unit)\n            local spell = aura_env.spellIDs[spellID] or CauseseDB.trash_cc[spellID]\n            if spell then\n                if aura_env.rpspellIDs[spellID] and not UnitInParty(unit..\"target\") then return false end\n                local isPlayer = UnitIsPlayer(unit..\"target\")\n                local role = UnitGroupRolesAssigned(\"player\")\n                local _, _, position = WeakAuras.SpecRolePositionForUnit(\"player\")\n                local customName, categoryNumber, customSound, requiredRole, showTarget, important = unpack(spell)\n                if\n                (\n                    not aura_env.config.showImportant\n                    or important == aura_env.config.showImportant\n                )\n                and\n                (\n                    requiredRole == \"ALL\"\n                    or\n                    role == requiredRole\n                    or\n                    position == requiredRole\n                ) then\n                    if spellID then \n                        local guid = UnitGUID(unit)\n                        --scanevent to update correct unit that is being targeted by cast & cast count\n                        if aura_env.config.display ~= 2 then\n                            C_Timer.After(aura_env.config.delay, function() WeakAuras.ScanEvents(castType == \"cast\" and \"CAUSE_DELAY_CAST\" or \"CAUSE_DELAY_CHANNEL\", unit, guid, showTarget)end)\n                        end\n                        local mark = GetRaidTargetIndex(unit)\n                        if customSound and aura_env.config.enableSounds then\n                            if aura_env.config.singleSound then\n                                local duplicate = false\n                                for _, state in pairs(allstates) do\n                                    if state.spellID == spellID then\n                                        duplicate = true\n                                        break\n                                    end\n                                end\n                                if not duplicate then\n                                    PlaySoundFile(\"Interface\\\\AddOns\\\\SharedMedia_Causese\\\\sound\\\\\"..customSound, \"MASTER\")\n                                end\n                            else\n                                PlaySoundFile(\"Interface\\\\AddOns\\\\SharedMedia_Causese\\\\sound\\\\\"..customSound, \"MASTER\")\n                            end\n                        end\n                        allstates[guid] = {\n                            show = true,\n                            changed = true,\n                            progressType = \"timed\",\n                            duration = duration,\n                            expirationTime = expirationTime,\n                            targetName = showTarget and (isPlayer and WA_ClassColorName(unit..\"target\") or \"PET\"),\n                            castType = castType,\n                            raidMark = mark and ICON_LIST[mark]..\"16|t\" or \"\",\n                            notInterruptible = notInterruptible,\n                            spell = customName or \"\",\n                            spellName = name,\n                            icon = icon,\n                            isFocus = UnitExists(\"focus\") and UnitGUID(\"focus\") == guid or false,\n                            spellID = spellID,\n                            categoryNumber = categoryNumber or -1,\n                            autoHide = true,\n                        } \n                        return true\n                    end\n                end\n            end\n            \n        elseif event == \"UNIT_SPELLCAST_STOP\"\n        or event == \"UNIT_SPELLCAST_CHANNEL_STOP\"\n        or event == \"NAME_PLATE_UNIT_REMOVED\" then\n            local unit = ...\n            local state = allstates[UnitGUID(unit)]\n            if state then\n                state.show = false\n                state.changed = true\n                return true\n            end\n        end\n    end\nend",
