["triggers"] = {
{
["trigger"] = {
["type"] = "custom",
["custom_type"] = "stateupdate",
["debuffType"] = "HELPFUL",
["event"] = "Health",
["unit"] = "player",
["events"] = "TRIGGER:2:3:4:5:6",
["custom"] = "function(allStates, event, triggerNum, triggerStates)\n    if event == \"OPTIONS\" then\n        local max = math.random(3) \n        for i = 1, max do\n            local duration = math.random(15)\n            allStates[tostring(i)] = {\n                unit = \"player\",\n                stacks = math.random(6),\n                duration = duration,\n                expirationTime = GetTime() + duration,\n                icon = 132212,\n                progressType = \"timed\",\n                autoHide = true,\n                show = true,\n                changed = true,\n            } \n        end\n        return true\n    elseif triggerNum == nil or WeakAuras.IsOptionsOpen() then\n        return\n    end\n    \n    aura_env.triggers = aura_env.triggers or {}\n    aura_env.triggers[triggerNum] = CopyTable(triggerStates)\n    \n    -- make a fresh table of what allStates should be\n    local output = {}\n    for triggerNum, triggerStates in pairs(aura_env.triggers) do\n        for key, triggerState in pairs(triggerStates) do\n            local sourceUnit = triggerState.sourceUnit\n            local destUnit = triggerState.destUnit\n            local spellId = triggerState.spellId\n            if sourceUnit and destUnit\n            and (UnitInParty(destUnit) or UnitInRaid(destUnit) or UnitIsUnit(destUnit, \"player\"))\n            and (aura_env.config.friendly or not UnitIsFriend(\"player\", sourceUnit))\n            and (not aura_env.config.ignoreSelf or not UnitIsUnit(\"player\", sourceUnit))\n            then\n                local sourceGUID = UnitGUID(sourceUnit)\n                local destGUID = UnitGUID(destUnit)\n                if sourceGUID and destGUID then\n                    local spellId2targetKey = spellId..destGUID\n                    local state = output[spellId2targetKey]\n                    if not state then\n                        local npcId = select(6, strsplit('-', sourceGUID or ''))\n                        output[spellId2targetKey] = {\n                            unit = destUnit,\n                            sources = {[sourceGUID] = true},\n                            stacks = 1,\n                            spellId = spellId,\n                            icon = triggerState.icon,\n                            duration = triggerState.duration,\n                            expirationTime = triggerState.expirationTime,\n                            castType = triggerState.castType,\n                            interruptible = triggerState.interruptible,\n                            npcId = tonumber(npcId) or 0,\n                            progressType = \"timed\",\n                            autoHide = true\n                        }\n                    else\n                        if not state.sources[sourceGUID] then -- skip if the cast was handled from an other trigger\n                            state.sources[sourceGUID] = true\n                            state.stacks = state.stacks + 1\n                            if triggerState.expirationTime < state.expirationTime then\n                                state.expirationTime = triggerState.expirationTime\n                                state.duration = triggerState.duration\n                            end\n                        end\n                    end\n                end\n            end\n        end\n    end\n    \n    local markForRemove = {}\n    -- states not in output will be remove\n    for k in pairs(allStates) do\n        markForRemove[k] = true\n    end\n    -- update \"allStates\" with data from \"output\", keeping track of which states had an update\n    for spellId2targetKey, outputState in pairs(output) do\n        allStates:Update(spellId2targetKey, outputState)\n        markForRemove[spellId2targetKey] = nil\n    end\n    -- hide states missing in \"ouput\"\n    for k in pairs(markForRemove) do\n        allStates:Remove(k)\n    end\nend",
