["subeventSuffix"] = "_CAST_START",
["custom"] = "function(allstates)\n    local currentTime = GetTime()\n    if aura_env.lastTime and currentTime - aura_env.lastTime <= 0.1 then return end\n    aura_env.lastTime = currentTime\n    \n    local Core = aura_env.GetTDACore()\n    if not Core then return end\n    \n    -- Get other trigger states:\n    local GS = WeakAuras.GetTriggerStateForTrigger\n    local S = {\n        DarkTransform = GS(aura_env.id, 3),\n        UnholyAssault = GS(aura_env.id, 4),\n        Apocalypse = GS(aura_env.id, 5),\n        Abomination = GS(aura_env.id, 6),\n        UnholyBlight = GS(aura_env.id, 7),\n        CryptFever = GS(aura_env.id, 8),\n    }\n    \n    -- Aura logic:\n    for unit in pairs(Core.supportedUnits) do\n        if UnitExists(unit) then\n            -- Self auras:\n            local UnholyAssault = Core.GetStateForUnit(S.UnholyAssault, unit)\n            local UnholyBlight = Core.GetStateForUnit(S.UnholyBlight, unit)\n            \n            -- Target auras:\n            local CryptFever = Core.GetMatchedAura(unit, S.CryptFever, true, false)\n            \n            -- Check for Necromancer's Bargain:\n            if CryptFever then\n                aura_env.bargainTracker = aura_env.bargainTracker or {}\n                aura_env.bargainTracker[UnitGUID(unit)] = true\n            end\n            local isBargain = aura_env.bargainTracker and aura_env.bargainTracker[UnitGUID(unit)]\n            \n            -- Casts:\n            local DarkTransform = Core.GetMatchedCast(unit, S.DarkTransform, true)\n            local Apocalypse = not isBargain and Core.GetMatchedCast(unit, S.Apocalypse, true) or nil\n            local Abomination = Core.GetMatchedCast(unit, S.Abomination, true)\n            \n            -- Aura display priority:\n            local state = DarkTransform or UnholyAssault or Apocalypse or Abomination or UnholyBlight\n            \n            -- Calculate danger level:\n            if state then\n                state.dangerLevel = 1\n                \n                if DarkTransform or UnholyAssault and (Apocalypse or CryptFever) then\n                    state.dangerLevel = 3\n                elseif UnholyAssault or Apocalypse or Abomination and CryptFever then\n                    state.dangerLevel = 2\n                end\n            end\n            \n            -- Get target text:\n            if state then\n                state.targetText = Core.GetTargetText(aura_env, unit)\n            end\n            \n            -- Add aura:\n            allstates[unit] = state\n            \n            -- Add extra aura:\n            if CryptFever and CryptFever.unit then\n                CryptFever.dangerLevel = state and state.dangerLevel or 1\n                CryptFever.index = 1\n                if state then state.index = 100 end\n                CryptFever.showStacks = true\n                CryptFever.targetText = Core.GetTargetText(aura_env, unit, CryptFever.unit)\n                allstates[unit .. \"_Extra\"] = CryptFever\n            else\n                allstates[unit .. \"_Extra\"] = nil\n            end\n        end\n    end\n    \n    return true\nend",
