["subeventSuffix"] = "_CAST_START",
["custom"] = "function(allstates)\n    local currentTime = GetTime()\n    if aura_env.lastTime and currentTime - aura_env.lastTime <= 0.1 then return end\n    aura_env.lastTime = currentTime\n    \n    local Core = aura_env.GetTDACore()\n    if not Core then return end\n    \n    -- Get other trigger states:\n    local GS = WeakAuras.GetTriggerStateForTrigger\n    local S = {\n        DecimatingBolt = GS(aura_env.id, 3),\n        DarkSoul = GS(aura_env.id, 4),\n        Havoc = GS(aura_env.id, 5),\n        Infernal = GS(aura_env.id,6),\n        \n    }\n    \n    -- Aura logic:\n    for unit in pairs(Core.supportedUnits) do\n        if UnitExists(unit) then\n            -- Self auras:\n            -- local DecimatingBolt = Core.GetStateForUnit(S.DecimatingBolt, unit)\n            local DarkSoul = Core.GetStateForUnit(S.DarkSoul, unit)\n            local Infernal = Core.GetMatchedCast(unit, S.Infernal, true)\n            \n            -- Debuffs:\n            \n            \n            local Havoc = Core.GetMatchedAura(unit, S.Havoc, true, true)\n            \n            -- Aura display priority:\n            local state = Havoc or Infernal\n            \n            -- Combine aura texts and set Decimating Bolt stacks to Dark Soul state:\n            --   if state then\n            --     state.stacks = DecimatingBolt and DecimatingBolt.stacks or 0\n            --   state.showStacks = true\n            -- state.combineTexts = DarkSoul and DecimatingBolt and { DecimatingBolt.triggernum, DarkSoul.triggernum } or nil\n            \n            \n            -- Calculate danger level:\n            if state then\n                state.dangerLevel = 1\n                \n                if Infernal\n                then\n                    state.dangerLevel = 3\n                    \n                elseif\n                Havoc then\n                    state.dangerLevel = 2\n                    \n                    \n                    \n                    \n                    \n                end\n            end\n            \n            -- Get target text:\n            if state then\n                state.targetText = Core.GetTargetText(aura_env, unit)\n            end\n            \n            -- Add aura:\n            allstates[unit] = state\n        end\n    end\n    \n    return true\nend\n\n\n\n",
