["init"] = {
["custom"] = "-- ###### DO NOT CHANGE BELOW ######\n\nlocal version = 1.1\nlocal isDebug = false\n\nif aura_env.TeeaDangerAlertsCore and aura_env.TeeaDangerAlertsCore.version and aura_env.TeeaDangerAlertsCore.version > version then\n    return\nend\naura_env.TeeaDangerAlertsCore = aura_env.TeeaDangerAlertsCore or {}\nlocal Core = aura_env.TeeaDangerAlertsCore\nCore.version = version\n\nlocal supportedUnits = {}\nfor i=1, 10 do supportedUnits[\"arena\" .. i] = true end\nif isDebug then\n    supportedUnits[\"player\"] = true\n    for i=1, 40 do supportedUnits[\"nameplate\" .. i] = true end\nend\nCore.supportedUnits = supportedUnits\n\n-- Enum\nCore.DangerLevel = {\n    None = 0,\n    Low = 1,\n    Medium = 2,\n    High = 3,\n}\n\n-- ##### Trigger API #####\nCore.TriggerAPI = {}\n\nlocal function CacheTriggerDisplayNames(env, triggerData)\n    if not env.triggerNameMap then\n        env.triggerNameMap = {}\n        for i, v in pairs(triggerData) do\n            env.triggerNameMap[v[1]] = v[2]\n        end\n    end\nend\n\nlocal GS = WeakAuras.GetTriggerStateForTrigger\n\n---Caches trigger display names and returns table with trigger states for provided aura_env and triggerData\n---@param env table #aura_env\n---@param triggerData table #Trigger data in this format: { Key = {triggerNum, triggerDisplayName} }, for example: { DarkAscension = {3, \"Dark\"}, VoidForm = {4, \"Void\"}, }\n---@return table #Returns table with trigger states in this format: { Key = triggerStates }\nCore.TriggerAPI.GS = function(env, triggerData)\n    -- Check if env is not nil before accessing it\n    if not env then return end\n    \n    -- Cache short display names for all triggers from triggerData:\n    CacheTriggerDisplayNames(env, triggerData)\n    \n    -- Build table with trigger states from triggerData:\n    local id = env.id\n    local result = {}\n    for i, v in pairs(triggerData) do\n        result[i] = GS(id, v[1])\n    end\n    return result\nend\n\n\n---Returns trigger state for specific provided unit. This is useful to get state for self auras, like Dark Ascension on enemy Shadow Priest.\n---@param states table #Trigger states\n---@param unit string #Unit string\n---@return table|nil #Returns trigger state for specific provided unit\nCore.TriggerAPI.GetStateForUnit = function(states, unit)\n    for _, state in pairs(states) do\n        if state.unit == unit then\n            return state\n        end\n    end\nend\n\n---Returns trigger state for specific matched aura with optional match by caster and target. This is useful to get state for target auras, like Mindgames from enemy Shadow Priest on you or your teammates.\n---@param states table #Trigger states\n---@param unit string #Unit string\n---@param isMatchCaster boolean #Match by caster\n---@param isMatchTarget boolean #Match by target\n---@param specificTarget string #Specific target unit\n---@return table|nil #Returns trigger state for specific matched aura with optional match by caster and target\nCore.TriggerAPI.GetMatchedAura = function(states, unit, isMatchCaster, isMatchTarget, specificTarget)\n    if not states then return end\n    local targetUnit = specificTarget or isMatchTarget and unit .. \"target\"\n    for _, state in pairs(states) do\n        if (\n            (not isMatchCaster or state.unitCaster and UnitIsUnit(state.unitCaster, unit))\n            and (not targetUnit or state.unit and UnitIsUnit(state.unit, targetUnit))\n        ) then\n            return state\n        end\n    end\nend\n\n---Returns trigger state for specific matched cast. This is useful to get state for casts made by enemy, for example Abomination summon from enemy Unholy DK.\n---@param states table #Trigger states\n---@param unit string #Unit string\n---@param isMatchCaster boolean #Match by caster\n---@return table|nil #Returns trigger state for specific matched cast\nCore.TriggerAPI.GetMatchedCast = function(states, unit, isMatchCaster)\n    if not states then return end\n    for _, state in pairs(states) do\n        if (\n            (not isMatchCaster or state.sourceGUID and state.sourceGUID == UnitGUID(unit))\n        ) then\n            return state\n        end\n    end\nend\n\n\n-- ##### Backward Compatibility #####\nCore.GetStateForUnit = Core.TriggerAPI.GetStateForUnit\nCore.GetMatchedAura = function(unit, states, isMatchCaster, isMatchTarget, specificTarget) return Core.TriggerAPI.GetMatchedAura(states, unit, isMatchCaster, isMatchTarget, specificTarget) end\nCore.GetMatchedCast = function(unit, states, isMatchCaster) return Core.TriggerAPI.GetMatchedCast(states, unit, isMatchCaster) end\n\n\nlocal classMap = {\n    [\"DEMONHUNTER\"] = \"DH\",\n    [\"DEATHKNIGHT\"] = \"DK\",\n}\nCore.GetTargetText = function(env, unit, targetUnit)\n    local text\n    \n    targetUnit = targetUnit or (unit .. \"target\")\n    if UnitExists(targetUnit) and UnitIsFriend(targetUnit, \"player\") then\n        if UnitIsUnit(targetUnit, \"player\") then -- Player\n            text = \"You\"\n        elseif UnitIsPlayer(targetUnit) then -- Other players\n            if env.config.isClassNamesTargetText then\n                local _, classText = UnitClass(targetUnit)\n                text = classText and classMap[classText] or classText\n            else\n                text = UnitName(targetUnit)\n            end\n        end\n    end\n    \n    if text then \n        -- Shorten:\n        if env.config.maxTargetTextLength > 0 and not env.config.isClassNamesTargetText then\n            text = WA_Utf8Sub(text, env.config.maxTargetTextLength)\n        end\n        \n        -- Class color:\n        if env.config.isClassColoredTargetText then\n            text = GetClassColoredTextForUnit(targetUnit, text)\n        end\n    end\n    \n    return text\nend\n\nCore.FormatText = function(env, triggerNum, targetText)\n    -- Check and initialize env.state if it's nil\n    if not env.state then\n        env.state = {}\n    end\n    \n    local mappedVal\n    if not env or not env.triggerNameMap then return end\n    if env.state and env.state.combineTexts then\n        local textA = env.state.combineTexts[1]\n        textA = type(textA) == \"string\" and textA or env.triggerNameMap[textA]\n        local textB = env.state.combineTexts[2]\n        textB = type(textB) == \"string\" and textB or env.triggerNameMap[textB]\n        mappedVal = string.format(\"%s\\n%s\", textA, textB)\n    else\n        mappedVal = triggerNum and env.triggerNameMap[triggerNum]\n    end\n    if not mappedVal then return end\n    \n    local text = mappedVal\n    \n    if env.state and env.state.extraText then\n        text = string.format(\"%s\\n%s\", text, env.state.extraText)\n    end\n    \n    if targetText and env.config.isShowTargetText then\n        text = string.format(\"%s\\n%s\", text, targetText)\n    end\n    \n    return text\nend\n\nCore.GetActiveTriggers = function(env)\n    local activeTriggers = WeakAuras.GetActiveTriggers(env.id)\n    local count = 0\n    for _, v in pairs(activeTriggers) do\n        if v then count = count + 1 end\n    end\n    return activeTriggers, count\nend\n\nCore.TeeaGlowFix = function(env, region)\n    if not env or not region then return end\n    if not env.config.isApplyGlowFix then return end\n    for _, v in pairs(region.subRegions) do\n        local f = v.glow and v._ButtonGlow\n        if f then\n            local width, height = v:GetSize()\n            f.ants:SetSize(width*1.4 * 0.88, height*1.4 * 0.88)\n            f.ants:SetPoint(\"CENTER\", 0, -0.5)\n        end\n    end\n    region.applyTeeaGlowFix = nil\nend\n\nCore.QueueGlowFix = function(env, region)\n    if not env or not region then return end\n    region.applyTeeaGlowFix = env.config.isApplyGlowFix\nend\n\nCore.isLoaded = true\n\nWeakAuras.ScanEvents(\"TEEA_DANGER_ALERTS_ENV\", Core)",
["do_custom"] = true,
},
