["adjustedMax"] = "",
["customText"] = "function()\n    if not aura_env.state then return end\n    local Core = aura_env.GetTDACore()\n    if not Core then return end\n    \n    local text = Core.FormatText(aura_env, aura_env.state.triggernum, aura_env.state.targetText) or aura_env.state.name\n    return text and text:upper()\nend",
["yOffset"] = 0,
["anchorPoint"] = "CENTER",
["cooldownSwipe"] = false,
["cooldownEdge"] = false,
["icon"] = true,
["triggers"] = {
{
["trigger"] = {
["type"] = "custom",
["custom_type"] = "stateupdate",
["debuffType"] = "HELPFUL",
["event"] = "Health",
["subeventPrefix"] = "SPELL",
["subeventSuffix"] = "_CAST_START",
["custom"] = "function(allstates)\n    local currentTime = GetTime()\n    if aura_env.lastTime and currentTime - aura_env.lastTime <= 0.1 then return end\n    aura_env.lastTime = currentTime\n    \n    local Core = aura_env.GetTDACore()\n    if not Core then return end\n    \n    -- Get other trigger states:\n    local GS = WeakAuras.GetTriggerStateForTrigger\n    local S = {\n        Clones = GS(aura_env.id, 3),\n        Serenity = GS(aura_env.id, 4),\n       -- Dance = GS(aura_env.id, 5),\n        Keefer = GS(aura_env.id, 6),\n        BonedustOnPlayer = GS(aura_env.id, 7),\n        Bonedust = GS(aura_env.id, 8),\n        Mark = GS(aura_env.id, 9),\n        WWSpecBuff = GS(aura_env.id, 10),\n    }\n    \n    -- Aura logic:\n    for unit in pairs(Core.supportedUnits) do\n        if UnitExists(unit) then\n            -- WW only spec aura (I really don't want to use GetArenaOpponentSpec and rather rely on WA to do it):\n            local WWSpecBuff = Core.GetStateForUnit(S.WWSpecBuff, unit)\n            \n            if WWSpecBuff then\n                -- Self auras:\n                local Clones = Core.GetStateForUnit(S.Clones, unit)\n                local Serenity = Core.GetStateForUnit(S.Serenity, unit)\n                local MainCD = Serenity or Clones\n                --local Dance = Core.GetStateForUnit(S.Dance, unit)\n                \n                -- Target auras:\n                local Keefer = Core.GetMatchedAura(unit, S.Keefer, true, true)\n                local BonedustOnPlayer = Core.GetMatchedAura(unit, S.Bonedust, true, false)\n                local Bonedust = Core.GetMatchedAura(unit, S.Bonedust, true, true)\n                \n                -- Count marks from same WW monk:\n                local markCount = 0\n                if S.Mark then\n                    for _, state in pairs(S.Mark) do\n                        if state.unitCaster and UnitIsUnit(state.unitCaster, unit) then\n                            markCount = math.min(5, markCount + 1)\n                        end\n                    end\n                end\n                \n                -- Aura display priority:\n                local state = MainCD or Keefer\n                \n                -- Set Mark count to main state stacks:\n                if state then\n                    state.stacks = markCount\n                    state.showStacks = true\n                end\n                \n                -- Combine aura texts:\n                if state then\n                    if MainCD and Keefer then\n                        state.combineTexts = { MainCD.triggernum, Dance or Keefer and Keefer.triggernum } \n                    elseif Dance and Keefer then\n                        state.combineTexts = {Keefer.triggernum} \n                    else\n                        state.combineTexts= nil\n                    end\n                end\n                \n                -- Calculate danger level:\n                if state then\n                    if Serenity or Clones then\n                        state.dangerLevel = 3  -- High danger level for Serenity\n                    else\n                        state.dangerLevel = 1  -- Default danger level\n                        \n                        \n                        if MainCD and Bonedust or MainCD and Keefer or MainCD and Dance or Dance and Keefer and markCount >= 3 then\n                            state.dangerLevel = 3\n                        elseif MainCD or Dance and Keefer or Dance and markCount >= 3 then\n                            state.dangerLevel = 2\n                        end\n                    end\n                end\n                \n                -- Get target text:\n                if state then\n                    state.targetText = Core.GetTargetText(aura_env, unit)\n                end\n                \n                -- Add aura:\n                allstates[unit] = state\n                \n                -- Add extra aura:\n                if BonedustOnPlayer and BonedustOnPlayer.unit then\n                    BonedustOnPlayer.dangerLevel = state and state.dangerLevel or 1\n                    BonedustOnPlayer.index = 1\n                    if state then state.index = 100 end\n                    BonedustOnPlayer.showStacks = false\n                    BonedustOnPlayer.targetText = Core.GetTargetText(aura_env, unit, BonedustOnPlayer.unit)\n                    allstates[unit .. \"_Extra\"] = BonedustOnPlayer\n                else\n                    allstates[unit .. \"_Extra\"] = nil\n                end\n            end\n        end\n    end\n    \n    return true\nend",
