["init"] = {
["custom"] = "aura_env.lastCheck = 0\naura_env.reminderThreshold = aura_env.config.reminderThreshold and aura_env.config.reminderThreshold * 60 or nil\n\nlocal eventIntervalInSeconds = 1.5 * 60 * 60\naura_env.eventEndAt = eventIntervalInSeconds - 15 * 60\n\n-- default EU sorry folks\nlocal regionalWeeklyStart = 1668985200 -- Mon Nov 21 2022 00:00:00 GMT+0100 (Central European Standard Time)\nregionalWeeklyStart = regionalWeeklyStart + 30 * 60\n\nif aura_env.config.region > 1 then\n    local regionOffsetMap = {\n        [2] = 3, -- us\n        [3] = 2, -- kr\n        [4] = 2, -- tw\n    } \n    \n    local offset = regionOffsetMap[aura_env.config.region]\n    \n    if offset then        \n        regionalWeeklyStart = regionalWeeklyStart - offset * 60 * 60 \n    end\nend\n\naura_env.calcSecondsToNextFeast = function()\n    return eventIntervalInSeconds - mod(GetServerTime() - regionalWeeklyStart, eventIntervalInSeconds)\nend\n\naura_env.hasWeeklyCompleted = function()\n    return C_QuestLog.IsQuestFlaggedCompleted(70893)\nend\n\naura_env.disabled = aura_env.config.disableOnCompletion and aura_env.hasWeeklyCompleted()\n\n-- initial check on login\nif aura_env.disabled or not aura_env.config.showTextReminder then\n    WeakAuras.ScanEvents(\"COMMUNITY_FEAST\", false, nil)    \nend\n\naura_env.nextFeastIn = aura_env.disabled and 0 or aura_env.calcSecondsToNextFeast()\n\naura_env.getTextSettings = function()\n    if not aura_env.config.showTextReminder then\n        return    \n    end\n    \n    local minutes = floor(aura_env.nextFeastIn / 60)    \n    local replaced = string.gsub(aura_env.config.reminderText, \"__MINUTES__\", minutes)\n    return replaced\nend",
