{
["trigger"] = {
["type"] = "custom",
["custom"] = "function()\n    local now = GetTime()\n    \n    -- Are we in combat?\n    local inCombat = UnitAffectingCombat(\"player\")\n    \n    -- Do we have a target, and is it an enemy?\n    local hasTarget = UnitExists(\"target\")\n    local isEnemy = false\n    if hasTarget then\n        -- UnitIsFriend(\"player\", \"target\") returns true if it's a friendly unit\n        -- So if it's *not* friend, we treat it as an enemy (or at least not friendly).\n        isEnemy = not UnitIsFriend(\"player\", \"target\")\n    end\n    \n    -- If we're in combat, always show\n    if inCombat then\n        aura_env.lastCombatEndTime = nil\n        return true\n    end\n    \n    -- If we have an enemy target, show\n    if isEnemy then\n        return true\n    end\n    \n    -- If we just left combat and haven't started the timer, set it\n    if not aura_env.lastCombatEndTime then\n        aura_env.lastCombatEndTime = now\n    end\n    \n    -- Check how long we've been out of combat\n    local timeOutOfCombat = now - aura_env.lastCombatEndTime\n    \n    -- If it's been less than 10 seconds since leaving combat, still show\n    if timeOutOfCombat < 10 then\n        return true\n    end\n    \n    -- Otherwise, hide\n    return false\nend\n\n\n",
