["custom"] = "function(allstates)\n    local currentTime = GetTime()\n    if aura_env.lastTime and currentTime - aura_env.lastTime <= 0.1 then return end\n    aura_env.lastTime = currentTime\n    \n    local Core = aura_env.GetTDACore()\n    if not Core then return end\n    \n    -- Get other trigger states:\n    local GS = WeakAuras.GetTriggerStateForTrigger\n    local S = {\n        Vendetta = GS(aura_env.id, 3),\n        SliceAndDice = GS(aura_env.id, 4),\n        Garrote = GS(aura_env.id, 5),\n        Rupture = GS(aura_env.id, 6),\n        Reprimand = GS(aura_env.id, 7)\n        \n    }\n    \n    -- Aura logic:\n    for unit in pairs(Core.supportedUnits) do\n        if UnitExists(unit) then\n            -- Self auras:\n            local SliceAndDice = Core.GetStateForUnit(S.SliceAndDice, unit)\n            local Reprimand = Core.GetMatchedCast(unit, S.Reprimand, true)\n            \n            -- Target auras:\n            local Vendetta = Core.GetMatchedAura(unit, S.Vendetta, true, false)\n            local Garrote, Rupture\n            if Vendetta then -- Get Garrote and Rupture only for unit with Vendetta\n                Garrote = Core.GetMatchedAura(unit, S.Garrote, true, true, Vendetta.unit)\n                Rupture = Core.GetMatchedAura(unit, S.Rupture, true, true, Vendetta.unit)\n            end\n            \n            -- Aura display priority:\n            local state = Vendetta or Reprimand\n            \n            -- Calculate danger level:\n            if state then\n                state.dangerLevel = 1\n                \n                if Vendetta then\n                    state.dangerLevel = 3\n                elseif Reprimand then\n                    state.dangerLevel = 2\n                end\n            end\n            \n            -- Get target text:\n            if state then\n                state.targetText = Core.GetTargetText(aura_env, unit)\n            end\n            \n            -- Add aura:\n            allstates[unit] = state\n        end\n    end\n    \n    return true\nend",
