{
["trigger"] = {
["type"] = "custom",
["custom_type"] = "stateupdate",
["debuffType"] = "HELPFUL",
["event"] = "Health",
["subeventPrefix"] = "SPELL",
["subeventSuffix"] = "_CAST_START",
["custom"] = "function(allstates)\n    local currentTime = GetTime()\n    if aura_env.lastTime and currentTime - aura_env.lastTime <= 0.1 then return end\n    aura_env.lastTime = currentTime\n    \n    local Core = aura_env.GetTDACore()\n    if not Core then return end\n    \n    local TriggerAPI = Core.TriggerAPI\n    local DangerLevel = Core.DangerLevel\n    \n    -- Get other trigger states:\n    local S = TriggerAPI.GS(aura_env, {\n            Meta =          {3, \"Meta\"},        -- Offensive CD\n            DemonSoul =     {4, \"Proc\"},        -- Passive +20% Damage\n            Initiative =    {5, \"+12% Crit\"},   -- Talent +12% Crit\n            Momentum =      {6, \"+8%\"},         -- Talent +8% Damage\n            EarthenWrit =   {7, \"Proc\"},        -- Weapon Enchant Proc\n            UnboundChaos =  {8, \"Fel Rush\"},    -- Not so dangerous anymore\n            EssenceBreak =  {9, \"Break\"},       -- Dangerous\n            SophicDevotion= {10, \"Proc\"}, --     Added by Gismo cause Weapon Enchant is used more\n    })\n    \n    -- Aura logic:\n    for unit in pairs(Core.supportedUnits) do\n        if UnitExists(unit) then\n            -- Self auras:\n            local Meta = TriggerAPI.GetStateForUnit(S.Meta, unit)\n            local DemonSoul = TriggerAPI.GetStateForUnit(S.DemonSoul, unit)\n            local Initiative = TriggerAPI.GetStateForUnit(S.Initiative, unit)\n            local Momentum = TriggerAPI.GetStateForUnit(S.Momentum, unit)\n            local EarthenWrit = TriggerAPI.GetStateForUnit(S.EarthenWrit, unit)\n            local UnboundChaos = TriggerAPI.GetStateForUnit(S.UnboundChaos, unit)\n            local SophicDevotion = TriggerAPI.GetStateForUnit(S.SophicDevotion, unit)\n            \n            \n            -- Target auras:\n            local EssenceBreak = TriggerAPI.GetMatchedAura(S.EssenceBreak, unit, true, false)\n            \n            -- Aura display priority:\n            local state = EssenceBreak or Meta or EarthenWrit or DemonSoul\n            \n            -- Calculate bonus damage:\n            local bonusDamage = 0\n            if DemonSoul then bonusDamage = bonusDamage + 20 end\n            if EarthenWrit then bonusDamage = bonusDamage + 20 end\n            if SophicDevotion then bonusDamage = bonusDamage + 20 end\n            if Initiative then bonusDamage = bonusDamage + 10 end   -- Rounded down to 10\n            if Momentum then bonusDamage = bonusDamage + 10 end     -- Rounded up to 10\n            \n            -- Combine aura texts:\n            if state then\n                if Meta and EssenceBreak then\n                    state.combineTexts = { bonusDamage >= 20 and string.format(\"Meta +%d%%\", bonusDamage) or Meta.triggernum, EssenceBreak.triggernum }\n                elseif (Meta or EssenceBreak) then\n                    if bonusDamage >= 20 then\n                        state.combineTexts = { (Meta or EssenceBreak).triggernum, string.format(\"+%d%%\", bonusDamage) }\n                    else\n                        state.combineTexts = nil\n                    end\n                elseif (EarthenWrit or DemonSoul) and bonusDamage >= 30 then\n                    state.combineTexts = { (EarthenWrit or DemonSoul).triggernum, string.format(\"+%d%%\", bonusDamage) }\n                else\n                    state.combineTexts = nil\n                end\n            end\n            \n            -- Calculate danger level:\n            if state then\n                state.dangerLevel = DangerLevel.Low\n                \n                if Meta and (DemonSoul or Initiative and Momentum or EarthenWrit or UnboundChaos or EssenceBreak) then\n                    state.dangerLevel = DangerLevel.High\n                elseif Meta or EssenceBreak then\n                    state.dangerLevel = DangerLevel.Medium\n                elseif bonusDamage >= 50 then\n                    state.dangerLevel = DangerLevel.Medium\n                end\n            end\n            \n            -- Get target text:\n            if state then\n                state.targetText = Core.GetTargetText(aura_env, unit)\n            end\n            \n            -- Add aura:\n            allstates[unit] = state\n        end\n    end\n    \n    return true\nend",
