["custom"] = "function(event, ... )\n    prefix, text, channel, sender, target, zoneChannelID, localID, name, instanceID = ...\n    \n    if prefix ~= aura_env.tresPrefix then return false end\n    local action, incLayer = text:sub(1,1), tonumber(text:sub(2,string.len(text)))\n    local myLayer = _G[\"TLS\"].layer;\n    \n    if action == \"I\" then \n        local _, class, layer, available,_ = strsplit(\"-\", text)\n        local n,r = strsplit(\"-\", sender, 2)\n        \n        local pi = {}\n        pi.class, pi.layer, pi.available, pi.time = class, layer, available, GetTime()\n        _G[\"TLS\"].guild[n] = pi;\n        return false\n    end\n    \n    if myLayer == nil then \n        return false\n    end \n    \n    for i=1, MAX_BATTLEFIELD_QUEUES do \n        status = GetBattlefieldStatus(i)\n        if status ~= \"none\" then return false end\n    end\n    \n    local shouldInvite = false\n    if action == \"A\" and incLayer and myLayer and myLayer ~= 0 and incLayer ~= myLayer then shouldInvite = true; end\n    if action == \"L\" and incLayer and myLayer and myLayer ~= 0 and incLayer == myLayer then shouldInvite = true; end\n    \n    if shouldInvite and isAvailable() == \"1\" then\n        InviteUnit(sender)\n    end\nend",
