["do_custom"] = true,
},
["init"] = {
["custom"] = "\naura_env.NWB = LibStub(\"AceAddon-3.0\").addons[\"NovaWorldBuffs\"]\naura_env.tresPrefix = \"TresLayerSwap\"\naura_env.defaultLayer = \"|cffff4800?|r\"\naura_env.tresLayer = aura_env.defaultLayer\naura_env.tresTicker = nil\n\n_G[\"TLS\"] = {}\n_G[\"TLS\"].guild = {}\n_G[\"TLS\"].layer = 0\n_G[\"TLS\"].layerCount = 0\n_G[\"TLS\"].initTicker = nil\n\nsuccessfulRequest = C_ChatInfo.RegisterAddonMessagePrefix(aura_env.tresPrefix)\n\n-- util functions\nfunction sortTable(t) \n    local st = {}\n    for k,v in pairs(t) do table.insert(st, {n = k, l = v.layer, a = v.available, c = v.class}) end\n    table.sort(st, function(a,b) \n            if a.a ~= b.a then \n                return a.a > b.a \n            end \n            return a.n:upper() < b.n:upper() \n    end)\n    \n    return st\nend\n\nfunction cleanupTable()\n    local delItems = {}\n    \n    for k,v in pairs(_G[\"TLS\"].guild) do\n        if GetTime() - v.time > 60 then \n            delItems[k] = true\n        end\n    end\n    \n    for k,v in pairs(delItems) do\n        _G[\"TLS\"].guild[k] = nil\n    end\nend\n\nfunction isAvailable()\n    if IsInInstance() then return \"0\" end\n    if IsInRaid() then return \"0\" end\n    if IsInGroup() and not (UnitIsGroupLeader(\"player\") or UnitIsGroupAssistant(\"player\")) then return \"0\" end\n    if MiniMapBattlefieldIcon and MiniMapBattlefieldIcon:IsVisible() then return \"0\" end\n    if MiniMapLFGFrame and MiniMapLFGFrame:IsVisible() then return \"0\" end\n    \n    return \"1\"\nend\n\nfunction sendTLSUpdate(from)\n    if not IsInGuild() then return end\n    C_ChatInfo.SendAddonMessage(\"TresLayerSwap\", \"I-\"..UnitClass(\"player\") .. \"-\" .. _G[\"TLS\"].layer .. \"-\" .. isAvailable() .. \"-\" .. from, \"GUILD\")\n    setglobal(\"TLSLayer\", _G[\"TLS\"].layer)\nend\n\n\n--======----\n\n-- Init buttons ---\nfunction initMainButton()\n    -- frame\n    local button = CreateFrame(\"BUTTON\", nil, aura_env.region)\n    button:RegisterForClicks(\"AnyUp\")\n    \n    local bgTex = button:CreateTexture(nil, \"BACKGROUND\")\n    bgTex:SetAllPoints()\n    bgTex:SetColorTexture(.2, .2, .2, 0.9)\n    \n    local hoverTex = button:CreateTexture(nil, \"HIGHLIGHT\")\n    hoverTex:SetAllPoints()\n    hoverTex:SetColorTexture(.3, .3, .3, 0.9)\n    button:SetHighlightTexture(hoverTex)\n    \n    return button\nend\n\nfunction createMenuButton(parent, index) \n    local button = CreateFrame(\"BUTTON\", nil, parent);\n    button:RegisterForClicks(\"AnyUp\")\n    \n    button:SetPoint(\"TOPLEFT\",parent, \"RIGHT\", 10, -32*(index-1) - 5)\n    button:SetSize(100,32)\n    \n    local fontString = button:CreateFontString(nil, \"OVERLAY\", \"GameFontNormal\")\n    fontString:SetPoint(\"CENTER\")\n    fontString:SetText(\"Layer \" .. index)\n    button.fontString = fontString\n    \n    local buttonTex = button:CreateTexture(nil, \"BACKGROUND\")\n    buttonTex:SetAllPoints()\n    \n    if index % 2 == 0 then \n        buttonTex:SetColorTexture(.18, .18, .18, 0.9)\n    else\n        buttonTex:SetColorTexture(.15, .15, .15, 0.9)\n    end\n    \n    \n    local hoverTex = button:CreateTexture(nil, \"HIGHLIGHT\")\n    hoverTex:SetAllPoints()\n    hoverTex:SetColorTexture(.3, .3, .3, 0.9)\n    button:SetHighlightTexture(hoverTex)\n    \n    button:SetScript(\"OnEnter\", function(self)\n            if button.fontString:GetText() == \"GhettoHS\" or button.fontString:GetText() == \"Close\" then return end\n            \n            local x = button:GetRight() + 140\n            local anchor = x < GetScreenWidth() and \"ANCHOR_BOTTOMRIGHT\" or \"ANCHOR_BOTTOMLEFT\"\n            GameTooltip:SetOwner(self, anchor)\n            GameTooltip:SetText(\"Players in Layer \" .. index)\n            \n            local sorted = sortTable(_G[\"TLS\"].guild)\n            for k,v in ipairs(sorted) do \n                if tonumber(v.l) == index then \n                    local c = v.a == \"1\" and RAID_CLASS_COLORS[v.c:upper()] or {r = 1.0, g = 0.0, b = 0.0}\n                    GameTooltip:AddLine(v.n, c.r,c.g,c.b)\n                end\n            end\n            \n            GameTooltip:Show();\n    end)\n    \n    button:SetScript(\"OnLeave\", function()\n            GameTooltip:Hide();\n    end)\n    \n    button:Hide()\n    return button\nend\n\nfunction placeMenu(mb)\n    local x,y = mb:GetCenter()\n    local width, height = 100, 32\n    local totalHeight = height*(_G[\"TLS\"].layerCount + 2)\n    \n    local xOffset, yOffset, anchor = 10, 32, \"RIGHT\"\n    if y - totalHeight < 0 then yOffset = totalHeight + height end\n    if x + width > GetScreenWidth() then xOffset = -width - 10; anchor = \"LEFT\"; end\n    \n    -- +2 getthohs and close\n    for i = 1, _G[\"TLS\"].layerCount+2 do \n        mb.menuButtons[i]:SetPoint(\"TOPLEFT\", mb, anchor, xOffset, yOffset - 32*i -5)\n    end\nend\n\n\nfunction hideMenuButtons(menuButtons)\n    for k,b in pairs(menuButtons) do\n        b:Hide()\n    end\nend\n\nfunction showMenuButtons(mb) \n    placeMenu(mb)\n    local numLayers = _G[\"TLS\"].layerCount\n    for i = 1, numLayers do\n        mb.menuButtons[i].fontString:SetText(\"Layer \" .. i)\n        mb.menuButtons[i]:Show()\n    end\n    mb.menuButtons[numLayers+1].fontString:SetText(\"GhettoHS\")\n    mb.menuButtons[numLayers+2].fontString:SetText(\"Close\")\n    \n    mb.menuButtons[numLayers+1]:Show()\n    mb.menuButtons[numLayers+2]:Show()\nend\n\nif not aura_env.region.tresButton then\n    mainButton = initMainButton()\n    \n    menuButtons = {}\n    for i=1, 20 do\n        menuButtons[i] = createMenuButton(mainButton, i)\n    end\n    \n    mainButton.menuButtons = menuButtons\n    aura_env.region.tresButton = mainButton\n    \n    for k,b in pairs(menuButtons) do\n        b:SetScript(\"OnCLick\", function(self,button)\n                local action = self.fontString:GetText()\n                if action == \"GhettoHS\" then\n                    InviteUnit(\"HS\");\n                    C_Timer.After(0.1,function() LeaveParty() end)\n                elseif action == \"Close\" then\n                else\n                    local l = string.match (action, \"%d+\")\n                    success = C_ChatInfo.SendAddonMessage(\"TresLayerSwap\", \"L\"..l, \"GUILD\")\n                    print(\"|cff20b2aaRequest to swap to layer \" .. l .. \" sent|r\")\n                end\n                hideMenuButtons(menuButtons)\n        end)\n    end\nend\n\n-- event onclick\naura_env.region.tresButton:SetAllPoints()\naura_env.region.tresButton:SetScript(\"OnClick\", function(self, button)\n        if button == \"LeftButton\" then\n            success = C_ChatInfo.SendAddonMessage(\"TresLayerSwap\", \"A\".._G[\"TLS\"].layer, \"GUILD\")\n            print(\"|cff20b2aaRequest to swap any layer sent|r\")\n        elseif button == \"RightButton\" then\n            showMenuButtons(self)\n        end\n    end\n)\n\n-- event enter\naura_env.region.tresButton:SetScript(\"OnEnter\", function(self)\n        GameTooltip:SetOwner(self, \"ANCHOR_TOP\")\n        GameTooltip:SetText(\"Current Layer \" .. _G[\"TLS\"].layer)\n        GameTooltip:AddLine(\"LClick: Request random layer\", 1, 1, 1)\n        GameTooltip:AddLine(\"RClick: Request specific layer / GhettoHS \", 1, 1, 1)\n        GameTooltip:AddLine(\"Red name: currently occupied\", 1,1,1)\n        \n        GameTooltip:Show();\nend)\n\n--event leave\naura_env.region.tresButton:SetScript(\"OnLeave\", function()\n        GameTooltip:Hide();\nend)\n\n-- init heartbeat ticker\nif not aura_env.tresTicker then\n    aura_env.tresTicker = C_Timer.NewTicker(60, function() \n            sendTLSUpdate(\"ticker\")\n            cleanupTable()\n    end)\nend\n\nif not _G[\"TLS\"].initTicker then\n    _G[\"TLS\"].initTicker = C_Timer.NewTicker(1, function()\n            if _G[\"NWB_CurrentLayer\"] and _G[\"NWB_CurrentLayer\"] ~= 0 then\n                _G[\"TLS\"].layer = _G[\"NWB_CurrentLayer\"]\n                _G[\"TLS\"].initTicker:Cancel()\n                \n                sendTLSUpdate(\"init\")\n            end\n    end)\nend",
