["custom"] = "function(a, e, t)\n    local currEnergy = UnitPower(\"player\", 3)\n    local maxEnergy = UnitPowerMax(\"player\", 3)\n    local dur = 2 -- Duration for the ticker to show\n    \n    -- Conditionally update the ticker based on the specified events\n    if (e == \"UNIT_POWER_FREQUENT\" and t == \"player\" and currEnergy > (aura_env.lastEnergy or 0)) or\n    (e == \"ENERGYTICK\" and t == aura_env.id and currEnergy == maxEnergy) then\n        -- Initialize or update the ticker properties\n        a[\"\"] = a[\"\"] or { show = true, progressType = \"timed\" }\n        local s = a[\"\"]\n        s.changed = true\n        s.duration = dur\n        s.expirationTime = GetTime() + dur\n        \n        -- Ensure the timer to refresh the ticker is set up only once\n        if not aura_env.refreshTimer then\n            aura_env.refreshTimer = true\n            C_Timer.After(dur, function()\n                    aura_env.refreshTimer = false -- Reset the flag after the timer executes\n                    WeakAuras.ScanEvents(\"ENERGYTICK\", aura_env.id) -- Trigger the tick event for the next cycle\n            end)\n        end\n    end\n    \n    -- Store the current energy for comparison in the next update\n    aura_env.lastEnergy = currEnergy\n    \n    return true\nend",
