["Nameplate Updated"] = "function (self, unitId, unitFrame, envTable, modTable)\n    \n    --do nothing if the player isn't in combat\n    if (not Plater.IsInCombat()) then\n        return \n    end\n    \n    --do nothing if the unit isn't in combat\n    if (not unitFrame.InCombat) then\n        return\n    end\n    \n    --do nothing if the unit is the player it self\n    if (unitFrame.IsSelf) then\n        return\n    end\n    \n    local name, icon, count, dispellType, duration, expiry = AuraUtil.FindAuraByName(modTable.config.spell, unitId, \"PLAYER|HARMFUL\")\n    \n    \n    if name then\n        if not modTable.config.refresh_enabled then\n            Plater.SetBorderColor (unitFrame, modTable.config.color)\n        else\n            if expiry - GetTime()<= modTable.config.refresh_threshold then\n                Plater.SetBorderColor (unitFrame, modTable.config.refresh_color)\n            else\n                Plater.SetBorderColor (unitFrame, modTable.config.color)\n            end\n            \n        end\n        \n    else\n        Plater.SetBorderColor (unitFrame)\n    end\n    \nend\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n",
