["custom"] = "function(allstates, event, isLogin, subevent, _, sourceGUID, _, _, _, _, _, _, _, spellId, _, _, _, amount)\n    \n    if event == \"WA_DELAYED_PLAYER_ENTERING_WORLD\" or event == \"STATUS\" or event == \"OPTIONS\" then\n        \n        if isLogin then\n            if aura_env.saved.state then aura_env.saved.state = nil end\n        end\n        \n        local as = C_UnitAuras.GetPlayerAuraBySpellID(462131)\n        if as ~= nil then\n            as = as.applications\n        else\n            as = 0\n        end\n        \n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            \n            inversed = aura_env.config.inverse,\n            \n            TempestInternalStacks = aura_env.maxStacks,\n            \n            TempestStacks = aura_env.maxStacks,\n            TempestStacksInverse = 0,\n            TempestProcs = as,\n        }\n        if aura_env.saved.state and (aura_env.saved.state.TempestInternalStacks ~= nil) then \n            allstates[\"\"].TempestInternalStacks = aura_env.saved.state.TempestInternalStacks\n            \n            allstates[\"\"].TempestStacks = aura_env.saved.state.TempestInternalStacks\n            if (allstates[\"\"].TempestStacks < 1) then allstates[\"\"].TempestStacks = 1 end\n            allstates[\"\"].TempestStacksInverse = aura_env.maxStacks - allstates[\"\"].TempestStacks\n        else\n            aura_env.saved.state = {}\n        end\n        \n        if allstates[\"\"].inversed then\n            allstates[\"\"].UniversalTempestStacks = allstates[\"\"].TempestStacksInverse\n        else\n            allstates[\"\"].UniversalTempestStacks = allstates[\"\"].TempestStacks\n        end\n        \n        return true\n        \n    elseif (event == \"COMBAT_LOG_EVENT_UNFILTERED\" and sourceGUID == WeakAuras.myGUID) then\n        \n        if (subevent == \"SPELL_CAST_SUCCESS\" and aura_env.listOfSpenders[spellId]) then --Maelstrom Spenders\n            \n            allstates[\"\"].changed = true\n            \n            local cost = C_Spell.GetSpellPowerCost(spellId)\n            if cost[1].name == \"MAELSTROM\" then\n                cost = cost[1].cost\n            else\n                cost = cost[2].cost\n            end\n            \n            allstates[\"\"].TempestInternalStacks = allstates[\"\"].TempestInternalStacks - cost\n            if (allstates[\"\"].TempestInternalStacks < -91) then\n                allstates[\"\"].TempestInternalStacks = 1  \n            end\n            \n            allstates[\"\"].TempestStacks = allstates[\"\"].TempestInternalStacks\n            if (allstates[\"\"].TempestStacks < 1) then allstates[\"\"].TempestStacks = 1 end\n            allstates[\"\"].TempestStacksInverse = aura_env.maxStacks - allstates[\"\"].TempestStacks\n            \n            aura_env.saved.state.TempestInternalStacks = allstates[\"\"].TempestInternalStacks\n            \n            if allstates[\"\"].inversed then\n                allstates[\"\"].UniversalTempestStacks = allstates[\"\"].TempestStacksInverse\n            else\n                allstates[\"\"].UniversalTempestStacks = allstates[\"\"].TempestStacks\n            end\n            \n            return true\n            \n            \n        elseif not (spellId == 454015) then --Tempest\n            return false\n            \n        elseif subevent == \"SPELL_AURA_REMOVED\" then --Update aura\n            return true\n            \n        elseif ((subevent == \"SPELL_AURA_APPLIED\" or subevent == \"SPELL_AURA_REFRESH\") and (spellId == 454015)) then --Tempest\n            allstates[\"\"].changed = true\n            \n            if (allstates[\"\"].TempestProcs == 0 and ((GetTime() - aura_env.nextTempestTime) <= 1)) then\n                aura_env.nextTempestTime = 0\n            else\n                allstates[\"\"].TempestInternalStacks = allstates[\"\"].TempestInternalStacks + aura_env.maxStacks;\n                if (allstates[\"\"].TempestInternalStacks > aura_env.maxStacks) then\n                    allstates[\"\"].TempestInternalStacks = aura_env.maxStacks\n                end\n                allstates[\"\"].TempestStacks = allstates[\"\"].TempestInternalStacks\n                allstates[\"\"].TempestStacksInverse = aura_env.maxStacks - allstates[\"\"].TempestStacks\n                aura_env.saved.state.TempestInternalStacks = allstates[\"\"].TempestInternalStacks\n                \n                if allstates[\"\"].inversed then\n                    allstates[\"\"].UniversalTempestStacks = allstates[\"\"].TempestStacksInverse\n                else\n                    allstates[\"\"].UniversalTempestStacks = allstates[\"\"].TempestStacks\n                end\n                \n            end\n            \n            return true\n            \n        end\n        \n    elseif event == \"TRIGGER\" then\n        local updatedTriggerStates = subevent\n        \n        for _, entry in pairs(updatedTriggerStates) do\n            \n            allstates[\"\"].TempestProcs = entry.totalStacks\n            if allstates[\"\"].TempestProcs == 0 then\n                aura_env.nextTempestTime = GetTime()\n            end\n            \n        end\n        \n        return true\n    end\n    \n    \nend",
