["init"] = {
["custom"] = "---- Parameters ----\n\nlocal ascentSpell = 372610\nlocal thrillBuff = 377234\nlocal thrillSpeed = 60\nlocal maxSamples = 5\nlocal ascentDuration = 3.5\nlocal ascentBoostMax = 35\nlocal pollRate = 1 / 10\nlocal updatePeriod = 1 / 10\nlocal showSpeed = aura_env.config.speedshow\nlocal hideBlizz = aura_env.config.hideblizz\n\nlocal speedTextTypeAir = aura_env.config.speedtype == 1\nlocal speedTextFormat, speedTextFactor = \"\", 1\nif aura_env.config.speedunits == 1 then\n    speedTextFormat = aura_env.config.speedshowunits and \"%.1fyd/s\" or \"%.1f\"\nelse\n    speedTextFormat = aura_env.config.speedshowunits and \"%.0f%%\" or \"%.0f\"\n    speedTextFactor = 100 / 7\nend\n\n---- Variables ----\n\nlocal active = false\nlocal updateHandle = nil\nlocal ascentStart = 0\nlocal lastX, lastY, lastT = 0, 0, 0\nlocal samples = 0\nlocal skipped = false\nlocal smoothSpeed, lastSpeed = 0, 0, 0\nlocal smoothAccel, lastAccel = 0, 0\n\n---- Localized functions ----\n\nlocal ScanEvents = WeakAuras.ScanEvents\nlocal GetTime = GetTime\nlocal After = C_Timer.After\n\n---- Trigger 1 ----\n\n-- Events:\n--   UNIT_SPELLCAST_SUCCEEDED:player\n--   DMUI_DRAGONRIDING_UPDATE\n\nlocal function setActive(allstates, state)\n    active = state\n    After(0, function()\n            ScanEvents(\"DMUI_DRAGONRIDING_SHOW\", state)\n    end)\n    \n    if active then\n        if hideBlizz and EncounterBar:IsVisible() then\n            aura_env.reshow = true\n            EncounterBar:Hide()\n        end\n        \n        if not updateHandle then\n            updateHandle = C_Timer.NewTicker(pollRate, function()\n                    if active then\n                        ScanEvents(\"DMUI_DRAGONRIDING_UPDATE\", true)\n                    end\n            end)\n        end\n        \n        if not allstates[\"\"] then\n            allstates[\"\"] = {\n                show = true,\n                changed = true,\n                progressType = \"static\",\n                value = 0,\n                accel = 0,\n                total = 100,\n                boosting = false,\n                thrill = false,\n                speedtext = \"\",\n                angle = \"\",\n            }\n            return true\n        end\n    else\n        if updateHandle then\n            updateHandle:Cancel()\n            updateHandle = nil\n        end\n        \n        if allstates[\"\"] then\n            allstates[\"\"].show = false\n            allstates[\"\"].changed = true\n            return true\n        end\n    end\nend\n\naura_env.cancelCallback = function()\n    if updateHandle then\n        updateHandle:Cancel()\n        updateHandle = nil\n    end\nend\n\naura_env.trigger1 = function(allstates, event, _, newAngle, spellId)\n    if event ~= \"DMUI_DRAGONRIDING_UDPATE\" then\n        if event == \"OPTIONS\" then\n            return setActive(allstates, false)\n        end\n        \n        if event == \"STATUS\" then\n            return setActive(allstates, true)\n        end\n        \n        -- Detect ascent boost\n        if event == \"UNIT_SPELLCAST_SUCCEEDED\" then\n            if spellId == ascentSpell then\n                ascentStart = GetTime()\n            end\n            return false\n        end\n    end\n    \n    local time = GetTime()\n    \n    -- Delta time\n    local dt = time - lastT\n    if dt < updatePeriod then\n        -- Rate limit speed updates!\n        return false\n    end\n    lastT = time\n    \n    if not allstates or not allstates[\"\"] then return false end\n    \n    -- Get flying speed\n    local isGliding, canGlide, forwardSpeed = C_PlayerInfo.GetGlidingInfo()\n    local speed = forwardSpeed\n    \n    local thrill = C_UnitAuras.GetPlayerAuraBySpellID(thrillBuff)\n    local boosting = thrill and time < ascentStart + ascentDuration\n    \n    -- Compute smooth speed\n    samples = math.min(maxSamples, samples + 1)\n    local lastWeight = (samples - 1) / samples\n    local newWeight = 1 / samples\n    smoothSpeed = speed\n    local newAccel = smoothSpeed - lastSpeed\n    lastSpeed = smoothSpeed\n    \n    -- Compute smooth acceleration\n    smoothAccel = smoothAccel * lastWeight + newAccel * newWeight\n    if speed > 63 then\n        -- Don't track negative acceleration when boosting\n        smoothAccel = max(0, smoothAccel)\n    end\n    if not IsFlying() then\n        smoothAccel = 0 -- Don't track acceleration on ground\n    end\n    lastAccel = smoothAccel\n    WeakAuras.ScanEvents(\"DMUI_DRAGONRIDING_ACCEL\", smoothAccel)\n    \n    -- Update display variables\n    local s = allstates[\"\"]\n    s.changed = true\n    s.value = smoothSpeed\n    s.boosting = boosting\n    s.thrill = not not thrill\n    if showSpeed then\n        s.speedtext = smoothSpeed < 1 and \"\" or string.format(speedTextFormat, smoothSpeed * speedTextFactor)\n    end\n    \n    return true\nend",
["do_custom"] = true,
},
